/*
 * @Author: your name
 * @Date: 2022-03-28 20:01:19
 * @LastEditTime: 2022-04-14 21:06:21
 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\layouts\BasicManage\index.js
 */
import React from 'react'
import { Layout, Button, Menu, Dropdown, NoticePanel, LogoutOutlined } from 'antd';
import { NavLink } from 'react-router-dom';
import { getCookie } from 'utils/dataTools';
const { Header, Sider, Content } = Layout;

class DesignManage extends React.Component {
    
    render() {
        const menu = (
            <Menu selectedKeys={[]} style={{ marginTop: '-12px' }} onClick={this.backToLogin}>
              <div style={{ textAlign: "center" }}>
                {getCookie('username')}
              </div>
              <Menu.Item key="logout">
                <LogoutOutlined />
                退出登录
              </Menu.Item>
            </Menu>
          );
        return (
            <Layout>
                <Header className="header">
                    <NavLink to='/basic'><Button type='dashed' className='backButton'>Back</Button></NavLink>
                    <Menu theme="light" mode="horizontal" defaultSelectedKeys={['2']} justify='center' className='headerMenu'>
                        <Menu.Item key="1">表单设计</Menu.Item>
                        <Menu.Item key="2">扩展功能</Menu.Item>
                        <Menu.Item key="3">表单发布</Menu.Item>
                    </Menu>
                    <div>
                        <Dropdown
                            // className={'noticePanel'}
                            placement="bottomCenter"
                            overlay={<span style={{ background: "#fff", padding: 10, boxShadow: '0 1px 10px #ccc', borderRadius: 7 }}>400-880-5687</span>} trigger={['hover']}>
                            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', borderRadius: '50%', cursor: 'pointer', border: '1px solid rgba(196,196,196,1)', width: 40, height: 40 }}>
                                <i className='iconfont icon-service' style={{ display: 'inline-block', color: '#9D9D9D' }}></i>
                            </span>
                        </Dropdown>
                        <Dropdown
                            // className={'noticePanel'}
                            placement="bottomCenter"
                            overlay={<NoticePanel
                                className='ImgPanel'
                                loading={false}
                                isImg={true}
                            />} trigger={['hover']}>
                            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', borderRadius: '50%', cursor: 'pointer', border: '1px solid rgba(196,196,196,1)', width: 40, height: 40 }}>
                                <i className='iconfont icon-weixin' style={{ display: 'inline-block', color: '#9D9D9D' }}></i>
                            </span>
                        </Dropdown>
                        <Dropdown
                            // className={'noticePanel'}
                            placement="bottomCenter"
                            overlay={menu} trigger={['hover']}>
                            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', borderRadius: '50%', cursor: 'pointer', border: '1px solid rgba(196,196,196,1)', width: 40, height: 40 }}>
                                <i className='iconfont icon-user' style={{ display: 'inline-block', color: '#9D9D9D' }}></i>
                            </span>
                        </Dropdown>
                    </div>
                </Header>
                {this.props.children}
            </Layout>
        );
    }
}

export default DesignManage;
