/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-01 20:45:23
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-19 05:32:22
 * @FilePath: \bl-device-manage-test\src\layouts\FormEdit\index.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { BulbTwoTone, CloseOutlined } from '@ant-design/icons';
import { Drawer, message, Tabs } from 'antd';
import FormRender, { useForm } from 'form-render';
import { defaultCommonSetting, defaultGlobalSettings, defaultGlobalSettingsi, defaultSettings } from 'constants/field_config';
import Generator from 'fr-generator';
import { inject, observer } from 'mobx-react';
import React, { useEffect, useRef, useState } from 'react';
import { Link_item } from './self_item/link_item';
import { toJS } from 'mobx';
import './index.css'
import { exChange, restore } from './changeTool';
import { Submit_check } from './self_item/submit_check';
import { Self_divider } from './self_item/self_divider';
import mul_tag from './self_item/mul_tag';

const { Provider, Sidebar, Canvas, Settings } = Generator;


const FormEdit = observer(({ HomeStore, FormStore }) => {
  const [schema, setSchema] = useState({});
  const [visible, setVisisble] = useState(false)
  const [lookItem, setLookItem] = useState({})
  const [schemaList,setSchemaList] = useState({})
  const [activeKey,setActiveKey] = useState('')
  let newArr = new Array(100);
  const ref = useRef();
  const refList = useRef();
  const form = useForm();
  const formList = useForm();
  useEffect(() => {
    FormStore.getFormField({ formId: HomeStore.firstFormId }).then(() => {
      FormStore.setValue('subFormList', JSON.parse(FormStore.formField.form.formFields))
      let nameArr = []
      console.log(restore(toJS(FormStore.formField)));
      let iList = restore(toJS(FormStore.formField))
      delete iList.root
      setSchemaList(iList);
      JSON.parse(FormStore.formField.form.formFields).map((item, index) => {
        if (item['name'] != 'root') {
          nameArr.push(toJS(item['name']));
        }
      })
      FormStore.setValue('subFormName', nameArr);
      setSchema(restore(toJS(FormStore.formField)))
    });
  }, []);
  const look = () => {
    console.log(toJS(FormStore.subFormList));
    let iObj = {...schemaList}
    iObj[activeKey] = refList.current.getValue();
    setSchemaList(iObj)
    setLookItem(ref.current.getValue())
    setVisisble(true);
  }
  const save = () => {
    console.log(schema);
    let params = exChange(ref.current.getValue(), HomeStore.firstFormId, 'root')
    console.log(toJS(FormStore.subFormList));
    FormStore.subFormList.map((item, index) => {
      console.log(toJS(item));
      if (item['name'] != 'root' && item['fields'].length != 0) {
        params['subForms'].push(toJS(item['fields']['subForms'][0]))
      } else if (item['name'] != 'root') {
        params['subForms'].push({ 'name': item['name'], 'fields': [] })
      }

    })
    FormStore.saveForm(params).then(() => {
      message.success('保存成功')
    })
    console.log(params);
  }
  const closeDrawer = () => {
    setVisisble(false)
  }

  const handleChange = (schema, title, index) => {
    let iList = [...FormStore.subFormList];
    let iiList = {...schemaList}
    iiList[title] = schema;
    setSchemaList(iiList);
    iList.splice(index, 1, { 'name': title, 'fields': exChange(schema, HomeStore.firstFormId, title) })
    FormStore.setValue('subFormList', iList)
  }
  const handleNew = (schame, index) => {
    newArr[index] = schame
  }
  const handleTagChange = (activeKey) => {
    console.log(refList.current.getValue());
    let iObj = {...schemaList}
    iObj[activeKey] = refList.current.getValue();
    setActiveKey(activeKey)
    setSchemaList(iObj)
    console.log(activeKey);
  }
  const getItem = () => {
    let newArr = []
    console.log(schemaList);
    for (const key in schemaList) {
      if (Object.hasOwnProperty.call(schemaList, key)) {
        const element = schemaList[key];
        let iObj = {}
        iObj['name'] = key
        iObj['schema'] = element;
        newArr.push(iObj);
      }
    }
    return (
      newArr.map((item,index) => {
        return (
          <Tabs.TabPane tab={item['name']} key={index}>
            <FormRender schema={item['schema']} widgets={{ self_divider: Self_divider }}
              form={formList} style={{ overflowY: 'auto' }} />
          </Tabs.TabPane>
        )
      })
    )
  }
  return (
    <>
      <div className='edit_header'>
        <div className='edit_left'>
          <BulbTwoTone className='icon_edit' />
          <a>查看新手引导</a>
        </div>
        <div className='edit_right'>
          <button className='edit_look' onClick={look}>预览</button>
          <button className='edit_save' onClick={save}>保存</button>
        </div>
      </div>
      <div className="fr-generator-playground" style={{ height: '88vh' }}>
        <Provider
          ref={ref}
          defaultValue={schema['root']}
          settings={defaultSettings}
          commonSettings={defaultCommonSetting}
          globalSettings={defaultGlobalSettings}
          extraButtons={[false, false, false, false]}
          controlButtons={[true, false, { text: 'up', onClick: (event, schema) => console.log(event, schema) }]}
          hideId={true}
          widgets={{
            link_item: Link_item,
            submit_check: Submit_check,
            self_divider: Self_divider,
            mul_tag: mul_tag
          }}
        >
          <div className="fr-generator-container" style={{ height: '88vh' }}>
            <Sidebar />
            <div className='edit_main' style={{ width: '100%' }}>
              <Canvas />
              <Tabs type='card' className='myTabs' onChange={handleTagChange} tabBarGutter={20} size='small'>
                {
                  FormStore.subFormList.map((item, index) => {
                    if (item['name'] != 'root') {
                      return (
                        <Tabs.TabPane tab={item['name']} key={item['name']}>
                          <div className="fr-generator-container">
                            <Provider
                              ref={refList}
                              defaultValue={schema[item['name']]}
                              settings={defaultSettings}
                              commonSettings={defaultCommonSetting}
                              onSchemaChange={(schema) => {
                                handleChange(schema, item['name'], index)
                                handleNew(schema, index);
                              }}
                              globalSettings={[]}
                              extraButtons={[false, false, false, false]}
                              controlButtons={[true, false, { text: 'up' }]}
                              hideId={true}
                              widgets={{
                                link_item: Link_item,
                                submit_check: Submit_check,
                                self_divider: Self_divider,
                              }}
                            >
                              <Canvas />
                              <Settings />
                            </Provider>
                          </div>
                        </Tabs.TabPane>
                      )
                    }
                  })
                }
              </Tabs>
            </div>
            <Settings />
          </div>
        </Provider>
      </div>

      <Drawer
        placement='bottom'
        size='large'
        width={1200}
        visible={visible}
        extra={
          <CloseOutlined onClick={closeDrawer} />
        }
      >
        <FormRender schema={lookItem} form={form} />
        <Tabs destroyInactiveTabPane={true} tabBarGutter={20}>
          {
            getItem()
          }
        </Tabs>
      </Drawer>
    </>
  );
});

export default inject((stores) => ({ HomeStore: stores.HomeStore, FormStore: stores.FormStore }))(FormEdit);
