import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Table, Divider, message, Modal, Badge } from 'antd';
import { toJS } from 'mobx';


@inject('SparepartsStore')
@observer
class SparepartsTable extends Component {
    render() {
        const { tableData, PageInfo, isLoading } = toJS(this.props.SparepartsStore)
        return <div style={{ marginTop: 20 }}>
            <Table  
                rowSelection={this.props.rowSelection}
                size='middle' rowKey="id" loading={isLoading} columns={this.getColumns()} dataSource={tableData} pagination={PageInfo} onChange={this.pageChange} />
        </div>;
    }
    pageChange = (e) => {
        this.props.SparepartsStore.PageInfo = e
        this.props.SparepartsStore.PageInfo.pageIndex = e.current
        const { getData, PageInfo, columnsType, typeName, spareName } = this.props.SparepartsStore
        columnsType === '1' ? getData(1, { typeName, pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize }) : getData(2, { spareName, pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize })
    }
    getColumns = () => {
        const { columnsType } = this.props.SparepartsStore
        if (columnsType === '2') {
            return [

                {
                    title: '备件名称',
                    dataIndex: 'spareName',
                    key: 'spareName',
                },
                {
                    title: '备件编号',
                    dataIndex: 'model',
                    key: 'model',
                },
                {
                    title: '类型',
                    dataIndex: 'spareTypeName',
                    key: 'spareTypeName',
                },
                // {
                //     title: '关联设备',
                //     dataIndex: 'deviceModelId',
                //     key: 'deviceModelId',
                // },
                // {
                //     title: '库存预警值',
                //     dataIndex: 'warnMax',
                //     key: 'warnMax',
                //     render: (text, record) => (
                //         <div>{record.warnMin ? record.warnMin : 0} - {record.warnMax ? record.warnMax : "∞"}</div>
                //     )
                // },
                {
                    title: '仓储位置',
                    dataIndex: 'address',
                    key: 'address',
                },
                {
                    title: '供应商',
                    dataIndex: 'supplier',
                    key: 'supplier',
                },
                {
                    title: '供货周期(月)',
                    dataIndex: 'period',
                    key: 'period',
                },
                // {
                //     title: '联系电话',
                //     dataIndex: 'phone',
                //     key: 'phone',
                // },
                {
                    title: '操作',
                    key: 'x',
                    align: 'right',
                    render: (text, record) => (
                        <span>
                            <a onClick={e => this.edit(2, record)}>详情</a>
                            <Divider type="vertical" />
                            <a onClick={e => this.edit(1, record)}>编辑</a>
                            {/* <Divider type="vertical" />
                           <a onClick={e => this.delete(record)}>删除</a>*/}
                        </span>
                    )

                }
            ]
        } else {
            return [
                {
                    title: '备件类型名称',
                    dataIndex: 'typeName',
                    key: 'typeName',
                },
                {
                    title: '状态',
                    dataIndex: 'typeStatus',
                    key: 'typeStatus',
                    render: (text) => (<div>
                        {text ? <Badge status="success" text="启用" /> : <Badge status="error" text="禁用" />}</div>)
                },
                {
                    title: '操作',
                    key: 'x',
                    align: 'right',
                    render: (text, record, index) => (
                        <span>
                            <a onClick={e => this.props.SparepartsStore.modalChange(1, record)}>编辑</a>
                            <Divider type="vertical" />
                            <a onClick={e => this.changeStatus(index, record)}>{record.typeStatus ? '禁用' : '启用'}</a>
                            {/*<Divider type="vertical" />
                          <a onClick={e => this.delete(record)}>删除</a>*/}
                        </span>
                    )

                }
            ]
        }
    }
    edit = (type, record) => {
        this.props.SparepartsStore.getAllType()
        this.props.SparepartsStore.modalChange(type, record)
    }
    changeStatus = (index, record) => {
        this.props.SparepartsStore.addNewType({ ...record, typeStatus: record.typeStatus ? 0 : 1 }).then(() => {
            message.success(record.typeStatus ? '禁用成功' : '启用成功')
            this.props.SparepartsStore.tableData[index].typeStatus = !record.typeStatus
        })
    }
    delete = (record) => {
        const { deleteType, deleteSpa, getData, PageInfo, columnsType, typeName, spareName } = this.props.SparepartsStore
        Modal.confirm({
            title: '提示',
            content: '是否确认删除该条数据',
            okText: '确认',
            cancelText: '取消',
            onOk() {
                if (columnsType === '1') {
                    deleteType({ id: record.id }).then(res => {
                        if (res) {
                            message.success('删除成功')
                            getData(1, { typeName, pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize })
                        }
                    })
                } else {
                    deleteSpa({ id: record.id }).then(res => {
                        if (res) {
                            message.success('删除成功')
                            getData(2, { spareName, pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize })
                        }
                    })
                }
            },
            onCancel() {
                console.log('Cancel');
            },
        });

    }
    componentDidMount() {
        const { getData, PageInfo, columnsType, typeName, spareName } = this.props.SparepartsStore
        columnsType === '1' ? getData(1, { typeName, pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize }) : getData(2, { spareName, pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize })
    }
}
export default SparepartsTable;
