import React from 'react'
import { Tabs, } from 'antd'
import { inspectList } from 'constants/configs'
import Check from './Check'
import Maintain from './Maintain'
import Repair from './Repair'
import Inspect from './Inspect'
import { initSearchQuery } from 'utils/dataTools';
import { inject,observer } from 'mobx-react';
import './index.less'
const { TabPane } = Tabs

@inject('DeviceMaintainStore')
@observer
class DeviceCheck extends React.Component{
	state = {
    tab : 1,
    car:false
	}
	render(){
		const { tab } = this.state;
		return <div >
			<Tabs 
				activeKey={this.state.tab.toString()} 
				onChange={this.handleTabs}
				size='small'
			>
			<TabPane tab={<div><i className="iconfont icon-checktype icon-spacing"></i>点检类型</div>} key="1" />
			<TabPane tab={<div><i className="iconfont icon-inspectiontype icon-spacing"></i>巡检类型</div>} key="2" />
			<TabPane tab={<div><i className="iconfont icon-maintenance icon-spacing"></i>保养类型</div>} key="3" />
			<TabPane tab={<div><i className="iconfont icon-faulttype icon-spacing"></i>故障类型</div>} key="4" />
			{/*
				{
					inspectList.map(tab => !tab.displayNone && 
					<TabPane key={`${tab.id}`}
						tab={tab.name} 
					>
					</TabPane>)
				}
			*/}
			</Tabs>
			<div className="tab-content-bg">
				{ tab === 1 && <Check />}
				{ tab === 2 && <Inspect />}
				{ tab === 3 && <Maintain />}
				{ tab === 4 && <Repair />}
			</div>
		</div>
	}

	handleTabs = key => {
		this.props.history.push('/basic/maintain?tab='+key)
		this.props.DeviceMaintainStore.maintainType = key
		this.setState({
			tab:parseFloat(key)
		})
	};
	componentDidMount(){
		this.query = this.props.location.search ? initSearchQuery(this.props.location.search) : { tab : '1'};
		this.setState({
			tab:parseFloat(this.query.tab)
		},()=>{
			this.props.DeviceMaintainStore.maintainType = this.state.tab;
			this.props.history.push('/basic/maintain?tab='+this.state.tab)
    })

	}
}

export default DeviceCheck
