/*
 * @Author: your name
 * @Date: 2022-03-31 23:08:16
 * @LastEditTime: 2022-04-05 21:06:31
 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\stores\SpareStore
 */
import { observable, action } from 'mobx';
import { isDataExist } from '../utils/dataTools';
import * as services from '../services/basic';

class Basic {
    @observable columns = [];
    @observable dataSource = [];

    @action.bound async queryAll(params) {
        try {
            let res = await services.getRequest(services.requestList.getFieldNameAndType, params);
            if (isDataExist(res)) {
                for (var i = 0; i < data[0].data.length; i++) {
                    var obj = {}
                    obj.title = data[0].data[0].label
                    obj.dataIndex = data[0].data[0].label
                    obj.key = data[0].data[0].label
                    columns.push(obj)
                }

                for (var i = 0; i < data.length; i++) {
                    var obj = {}
                    obj.key = (i + 1).toString()
                    for (var j = 0; j < data[i].data.length; j++) {
                        const label = data[i].data[j].label
                        obj[label] = data[i].data[j].attr.value
                    }
                    dataSource.push(obj)
                }
            }
        } catch (error) {
            console.log(error);
        }
    }


    @action.bound async addNew(params) {
        try {
            let res = await services.putRequest(services.requestList.saveDatabase, params);
            console.log(params);
            if (isDataExist(res)) {
                return res;
            }
        } catch (error) {
            console.log(error);
        }
    }
}

let BasicStore = new Basic();
export default BasicStore;
