/*
 * @Author: your name
 * @Date: 2022-04-02 11:12:08
 * @LastEditTime: 2022-04-07 19:35:17
 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\layouts\BasicManage\ComputerPage\index.js
 */

import { inject, observer } from 'mobx-react'
import React, { Component } from 'react'
import { Table, Modal, message } from 'antd'
import GlobalForm from 'components/GlobalForm';
import { MenuObj } from 'constants/configs';
import { checkCurrentMenu } from 'utils/dataTools';
import { toJS } from 'mobx';


@inject('BasicStore')
@inject('HomeStore')
@observer
class ComputerPage extends React.Component {

  render() {
    const { model } = this.props.HomeStore;
    const { dataSource } = this.props.BasicStore;
    const { PageInfo } = this.props.BasicStore;
    if (model == 'look') {
      return <Table columns={this.getColumns()} dataSource={dataSource} pagination={PageInfo} onChange={this.onChange}></Table>;
      // return <Table columns={this.getColumns()} dataSource={dataSource} pagination={PageInfo}></Table>;
    } else {
      return <GlobalForm />

    }


  }
  onChange = (e) => {
    this.props.BasicStore.PageInfo = e;
    this.props.BasicStore.PageInfo.pageIndex = e.current
    const {PageInfo} = this.props.BasicStore;
    console.log(PageInfo);
    this.getData()
  }
  getColumns = () => {
    const { columns } = this.props.BasicStore
    const operation = {
      title: '操作',
      key: 'x',
      align: 'right',
      render: (text, record) => (
        <span>
          {/* <a onClick={e => this.delete(record)}>删除</a> */}
          <a onClick={e => this.delete(record)}>删除</a>
        </span>
      )

    }
    columns.push(operation)
    return columns
  }
  delete = (txt) => {
    Modal.confirm({
      title: '提示',
      content: '是否确认删除该条数据',
      okText: '确认',
      cancelText: '取消',
      onOk() {
        message.success('删除成功')
        console.log(txt);
        console.log(txt.id);
      },
      onCancel() {
        console.log('Cancel');
      },
    });
  }

  getData = () => {
    const { PageInfo } = this.props.BasicStore;
    const { selectedKeys } = this.props.HomeStore;
    let actionId = selectedKeys[0];
    let menuObj = toJS(MenuObj.leafMenuModels);
    let current = checkCurrentMenu({ menuObj, actionId });
    let params = {};
    params.firstFormId = current.currentMenu[0].id.toString();
    params.pageIndex = PageInfo.pageIndex
    params.PageSize = PageInfo.PageSize
    this.props.BasicStore.queryAll(params);
  }
  componentWillMount() {
    this.getData();
  }
}

export default ComputerPage;
