import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Button, Row, Input, Modal, message } from 'antd';
import RoleTable from './RoleTable';
import RoleDialog from './RoleDialog';

@inject('SystemStore')
@observer
class RoleManager extends Component {
    render() {
        const { modalVisible, modalChange } = this.props.SystemStore
        return <div className="tab-content-bg">
            <Row>
                <Input allowClear style={{ width: 250 }} onChange={this.setRoleName} placeholder='请输入角色名称'></Input>
                <Button type='primary' style={{ marginLeft: 10 }} onClick={this.search} className='btn-border-purple'>查询</Button>
                <Button style={{ marginLeft: 10 }} onClick={e => modalChange(0)} className='add-btn' type='primary'>
                    <i className="iconfont icon-add icon-spacing" style={{fontSize:'12px'}}></i>
                    新增
                </Button>
                <Button style={{ marginLeft: 10 }} onClick={this.batchRemove} className="btn-border-gray">批量删除</Button>
            </Row>
            <RoleTable />
            {modalVisible && <RoleDialog />}
        </div>;
    }
    batchRemove = () => {
        const { deleteRole, getData, PageInfo, selectedRows } = this.props.SystemStore
        if (selectedRows.length === 0) {
            message.warning('请至少选择一条数据')
            return
        }
        Modal.confirm({
            title: '提示',
            content: `是否确认删除该${selectedRows.length}条数据？`,
            okText: '确认',
            cancelText: '取消',
            onOk: () => {
                let arr = []
                for (let item of selectedRows) {
                    arr.push(item.id)
                }
                deleteRole(0, { ids: arr }).then(res => {
                    message.success('删除成功')
                    getData(2, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize })
                })
            },
        });
    }
    setRoleName = (e) => {
        this.props.SystemStore.roleName = e.target.value
    }
    componentDidMount() {
        const { getTreeData } = this.props.SystemStore
        getTreeData()
    }
    search = () => {
        const { getData, PageInfo, roleName } = this.props.SystemStore
        getData(2, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, roleName })
    }
    componentWillUnmount() {
        this.props.SystemStore.clearData()
    }
}
export default RoleManager;
