import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Radio } from 'antd';
import MaintainServer from './Server'
import MaintainSchema from './Schema'
import MaintainOperate from './Operate'
import MaintainQuery from './Query'
import MaintainPlan from './Plan'

@inject('ZCloudDeviceStore')
@observer
class DeviceMaintain extends Component {
	state = {
		radio: 1
	}
	render() {
		const { radio } = this.state;
		return <div className='tabs_container'>
			<Radio.Group style={{ marginBottom: 20 }} defaultValue='1' onChange={this.handleRadio}>
				<Radio.Button value='1'>保养库</Radio.Button>
				<Radio.Button value='2'>保养方案</Radio.Button>
				<Radio.Button value='5'>保养计划</Radio.Button>
				<Radio.Button value='3'>保养操作</Radio.Button>
				<Radio.Button value='4'>保养结果</Radio.Button>
			</Radio.Group>
			{radio === 1 && <MaintainServer type='maintain' />}
			{radio === 2 && <MaintainSchema type='maintain' />}
			{radio === 3 && <MaintainOperate type='maintain' />}
			{radio === 4 && <MaintainQuery type='maintain' />}
			{radio === 5 && <MaintainPlan type='maintain' />}
		</div>
	}
	handleRadio = e => {
		this.setState({
			radio: parseFloat(e.target.value)
		})
	};
	componentWillMount() {
		this.props.ZCloudDeviceStore.modifyValue('planType', 2)
		this.props.ZCloudDeviceStore.modifyValue('maintainType', 2)
	}

}
export default DeviceMaintain;
