import React from 'react'
import { inject,observer } from 'mobx-react'
import { toJS } from 'mobx'
import { Tabs,Button } from 'components/BLComps'
import { Icon,Checkbox, Spin, Empty } from 'antd'
import Category from 'components/Category';
import { DraggableArea } from 'react-draggable-tags';
import './index.less'
import GlobalFooter from 'components/GlobalFooter';


@inject('TitleConfigStore')
@observer
class TitleConfigPage extends React.Component{
  constructor(props){
    super(props)
    this.store = this.props.TitleConfigStore
    this.state = {
      secTabKey:'1',
      tabs:[
        { tab:'设备管理', key: 1 },
      ],
      content : [ 
        { tab:'待办任务', key: 2 },
        { tab:'工单明细', key: 3 },
        { tab:'工单报告', key: 4 },
      ],
      tags : toJS(this.store.titleList),
      checkedList:[],
      tagsList:[],
      checkAll:false,
      indeterminate:false,

    }
  }
  render(){
    console.log(this.state.tags,toJS(this.store.titleList))
    const operations = <Button type='link' disabled><Icon type='plus' />新增</Button>
    return <div>
      <Spin spinning={this.store.isLoading}>
        <Tabs
          type='card'
          tabBarStyle = {{ height:30 }}
          dataSource={
            this.state.tabs
          }
          tabBarExtraContent={operations}
        />
        <div className='tab-content-bg'>
          <Tabs
            type='wrapper'
            defaultActiveKey = '1'
            dataSource = {
              [
                { tab:'设备台账',key:1 },
                { tab:'运维工单',key:'work' },
              ]
            }
            onChange={this.handleFirst}
            style={{marginBottom:10}}
          />
          {
            this.state.secTabKey === 'work' && <Category
              keyName='tab'
              title='选项'
              defaultActiveKey = {this.state.content[0]}
              content = {this.state.content}
              onClick={this.handleCates}
            />
          }
          <div className='title_tab_body'>  
            <div>
              <h4>请选择需要展示的列表表头项：</h4>
              <div style={{ borderBottom: '1px solid #E9E9E9',marginBottom:5 }}>
                <Checkbox
                  indeterminate={this.state.indeterminate}
                  onChange={this.onCheckAllChange}
                  checked={this.state.checkAll}
                >
                  全部
                </Checkbox>
              </div>
              {            
                this.state.tagsList.length > 0 && <Checkbox.Group
                  options={this.state.tagsList}
                  value={this.state.checkedList}
                  onChange={this.handleTitle}
                />
              }
            </div>
            <div>
              <h4>显示顺序（可拖拽实现title的排序）：</h4> 
              {this.state.checkedList.length > 0 ?<div className='Simple'>
                <DraggableArea
                  tags={this.state.tags}
                  render={({ tag, index }) => (
                    tag.checked && <div className='tag' key={tag.id}>
                      <img
                        src={require('assets/delete.png')}
                        alt='delete'
                        className='delete'
                        onClick={() => this.handleClickDelete(tag)}
                      />
                      {tag.titleValue}
                    </div>
                  )}
                  onChange={this.changeTag}
                /> 
              </div> : <div style={{border:'1px dashed #9d9d9d'}}>
                <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} description='暂无选项' />
              </div>}

            </div>
            <div className='tab_footer'>
              <Button type='primary' loading={this.store.isLoading} onClick={this.handleSubmit}>提交</Button>
            </div>
          </div>
        </div>
      </Spin>
    </div>
  }
  handleSubmit = () =>{
    let params = []
    this.state.tags.map((tag,index)=>{
      params.push({id:tag.id,checked:tag.checked,sort:index+1})
    })
    this.store.saveTitle({titleDOList:params},{menuId:this.state.secTabKey == 'work' ? 2 : this.state.secTabKey})
  }
  handleTitle = checkedList => {
    this.state.tags.map(tag =>{
      Object.assign(tag,{
        checked : false
      })
      checkedList.map(item=>{
        if(item === tag.titleKey){
          Object.assign(tag,{
            checked : true
          })
        }
      })
    })
    this.setState({
      checkedList,
      indeterminate: !!checkedList.length && checkedList.length < this.state.tags.length,
      checkAll: checkedList.length === this.state.tags.length,
    });
  };
  onCheckAllChange = e => {
    let checkedList = []
    if(e.target.checked){
      this.state.tags.map(item=>{
        Object.assign(item,{
          checked:true
        })
        checkedList.push(item.titleKey)
      })
    }else{
      this.state.tags.map(item=>{
        Object.assign(item,{
          checked:false
        })
      })
    }
    this.setState({
      checkedList,
      indeterminate: false,
      checkAll: e.target.checked,
    });
  }
  handleClickDelete = (tag) => {
    const tags = this.state.tags.filter(t => tag.id !== t.id);
    this.state.checkedList = this.state.checkedList.filter(t => t !== tag.titleKey)
		this.setState({ tags });
	}
  changeTag = tags =>{
    this.setState({
      tags
    })
  }
  handleCates = tag =>{
    this.getTitle({menuId:tag.key})
  }

  handleFirst = key  =>{
    this.setState({secTabKey:key})
    if(key === 'work') key = 2;
    this.getTitle({menuId:parseInt(key)})
  }
  getTitle = params =>{
    this.store.getTitle(params).then(()=>{
      this.state.tags = toJS(this.store.titleList)
      this.state.tagsList = []
      this.state.tags.map(item=>{
        if(item.checked){
          this.state.checkedList.push(item.titleKey)
        }
        this.state.tagsList.push({ ...item,label:item.titleValue,value:item.titleKey })
      })
      if(this.state.checkedList.length === this.state.tags.length){
        this.state.checkAll = true
      }
      this.setState({})
    })
  }
  componentDidMount(){
    this.getTitle({menuId:1})
  }
}

export default TitleConfigPage
