import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Tabs } from 'components/BLComps';
import { initSearchQuery } from 'utils/dataTools';
import MaintainPage from './MaintainPage'
import RepairPage from './RepairPage'
import { withRouter } from 'react-router-dom'
const TypeList = [
	{ tab:'点检配置', key:1 },
	{ tab:'保养配置', key:2 },
]
@withRouter
@inject('ZCloudDeviceStore')
@observer
class DeviceMaintain extends Component {
	state = {
		tab : 1
	}
	render(){
		const { tab } = this.state;
		return <div>
			<Tabs 
				activeKey={tab.toString()} 
				onChange={this.handleTabs}
				size='small'
				type="card"
				dataSource={TypeList}
			/>
				{/* {
					TypeList.map(tab => <TabPane {...tab}></TabPane>)
				} */}

			{ tab === 1 && <RepairPage/> }
			{ tab === 2 && <MaintainPage /> }
		</div>
	}
	handleTabs = key => {
		this.props.history.push('/iotstudio?tab='+key)
		this.setState({
			tab:parseFloat(key)
		})
	};
	componentDidMount(){
		this.props.ZCloudDeviceStore.getDevice();
		this.query = this.props.location.search ? initSearchQuery(this.props.location.search) : { tab : '1'};
		this.setState({
			tab:parseFloat(this.query.tab)
		},()=>{
			this.props.history.push('/iotstudio?tab='+this.state.tab)
		})
	}
	componentWillReceiveProps(nextProps){
		let query = initSearchQuery(nextProps.location.search)
		if(query.tab !== this.query.tab){
			nextProps.ZCloudDeviceStore.deviceType = nextProps.ZCloudDeviceStore.deviceList.length > 0 ? nextProps.ZCloudDeviceStore.deviceList[0].typeName : ''
		}
	}
}
export default DeviceMaintain;
