/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-17 11:56:33
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-18 17:05:11
 * @FilePath: \bl-device-manage-test\src\layouts\FormEdit\self_item\mul_tag.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { DeleteOutlined } from "@ant-design/icons";
import { Switch } from "antd";
import React, { useEffect, useState } from "react";
import './index.css'
export const Mul_Tag = ({ value, onChange, ...rest }) => {
    const { addons } = rest
    const [tagList, setTagList] = useState(value || [])
    useEffect(() => {
        console.log(rest);
    }, [])
    const getItem = () => {
        if (addons.dependValues[0] == true) {
            if (typeof (value) == 'array') {

            } else {
                onChange([{ title: '标签一', }, { title: "标签二" }, { title: '标签三' }])
            }

        }
    }
    const handleChange = (checked) => {
        console.log(checked);
        if (checked == true) {
            let list = [{ title: '标签一' }, { title: '标签一' }, { title: '标签一' }]
            setTagList(list)
            onChange(list);
        } else {
            setTagList([])
            onChange([]);
        }
    }
    return (
        <div className="total_tags">
            <Switch onChange={handleChange}>多标签显示</Switch>
            <div className="all_tags">
                {tagList.map((item, index) => {
                    <div className="one_tag">
                        <Input defaultValue={item['title']}/>
                        <DeleteOutlined />
                    </div>
                })}
            </div>
        </div>
    )
}
