/*
 * @Description: 
 * @version: 
 * @Author: zhihao
 * @Date: 2022-04-17 15:22:43
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-01 02:56:29
 */

import { observable, action, toJS } from 'mobx';
import * as services from '../services/home';


class Table {
	@observable isModalEdit = false; //保养数据的对话框是否为编辑状态
	@observable dataPageModalVis = false; //弹窗是否可见
	@observable selectedIdsList = [];   // 保养数据表格第一列的选择ID列表  
	@observable allSelectedIdsList = [];  //全选ID列表
	@observable selectedDate = '';//日历选中的日期
	@observable chosenDateRow = [];//选中日期的保养数据
	@observable chosenDateRowName = [];
	@observable selectedRowKeys = [];
	@observable columns = []; //表格使用的columns
	@observable dataSource = [];
	@observable columnsList = [];
	@observable showColumns = [];
	@observable lastColumns = [];
	@observable fieldValue = [];
	@observable model = "look"; //模式控制;
	@observable PageInfo = {pageSize: 5}



	@action getModel() {
		return this.model;
	}

	//修改展示模式
	@action.bound changeModel(value,firstFormId) {
		this.getAllData({formId:firstFormId})
		this.model = value;
	}

	@action.bound setFieldValue(value) {
		this.fieldValue = value
	}

	@action.bound setLastColumns() {
		this.lastColumns = []
		this.columns.forEach((item) => {
			if (this.fieldValue.indexOf(item.key) > -1) {
				this.lastColumns.push(item);
			}
		})
		// console.log();
	}

	//是否展示对话框
	@action.bound setDataPageModalVis(visible) {
		this.dataPageModalVis = visible;
	}

	@action.bound setModalEditData(data) {
		this.modalEditData = data;
	}

	//是否编辑对话框
	@action.bound setIsModalEdit(editble) {
		this.isModalEdit = editble;
	}

	@action.bound setSelectedRowKeys(keys) {
		this.selectedRowKeys = keys;
	}

	@action.bound async getAllData(params) {
		this.columns = [];
		this.dataSource = [];
		this.fieldValue = [];
		console.log(params);
		try {
			let res = await services.getRequest(services.requestList.getAllData, params);
			let data = res.data.data;
			this.columnsList = data.fields;
			data.fields.map((item) => {
				this.columns.push({ 'title': item.name, 'dataIndex': item.id, 'key': item.id });
				this.fieldValue.push(item.id)
			})
			this.fieldValue.push('createPerson')
			this.fieldValue.push('createTime')
			this.fieldValue.push('updateTime')
			this.columns.push({ 'title': '创建人', 'dataIndex': 'createPerson', 'key': 'createPerson' });
			this.columns.push({ 'title': '创建时间', 'dataIndex': 'createTime', 'key': 'createTime' });
			this.columns.push({ 'title': '更新时间', 'dataIndex': 'updateTime', 'key': 'updateTime' });
			data.fieldsValue.map((item) => {
				let obj = {}
				obj.createPerson = item.createPerson;
				obj.createTime = item.createTime;
				obj.key = item.id;
				let iObj = JSON.parse(toJS(item.formData))
				for (const front in iObj) {
					obj[front] = iObj[front]
				}
				this.dataSource.push(obj)
			})
			this.showColumns = this.columns;
			this.lastColumns = this.columns;
			console.log(toJS(this.dataSource));
			console.log(toJS(this.lastColumns));
			console.log(res);
		} catch (error) {
			console.log(error);
		}
	}


}

let TableStore = new Table();
export default TableStore;
