/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-08 06:52:37
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-08 09:17:01
 * @FilePath: \bl-device-manage-test\src\components\GlobalTabel2\sortLayout.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { Button, Popover } from 'antd'
import { toJS } from 'mobx'
import { inject, observer } from 'mobx-react'
import React, { useEffect, useState } from 'react'
import stores from 'stores'
import './index.css'
const SortLayout = observer(({ TableStore }) => {
    const [sortList, setSortList] = useState({});

    const handleAdd = (item) => {
        let iSort = sortList;
        iSort[item['key']] = item
        setSortList(iSort)
        console.log(sortList);
    }
    const menu = (
        <div className='columns_list'>
            {
                TableStore.columns.map((item, index) => {
                    console.log(item);
                    console.log(sortList);
                    if (sortList.hasOwnProperty(item['key'])) {
                        return (
                            <div className="columns_item" key={index}>
                                {item['title']}
                            </div>
                        )
                    } else {
                        return (
                            <div className="columns_item item_uncheck" onClick={() => handleAdd(item)} key={index}>
                                {item['title']}
                            </div>
                        )
                    }
                })
            }
        </div>
    )
    useEffect(() => {
        console.log(toJS(TableStore.columns));
        console.log(menu);
    }, [])
    const handleClick = () => {
        console.log('add');
    }
    return (
        <div className='sort_layout'>
            <Popover content={menu} trigger='click'>
                <div className='sort_add'>
                    <Button onClick={handleClick}>+添加排序规则</Button>
                </div>
            </Popover>
            <div className='sort_list'></div>
            <div className='sort_btn'>
                <Button type='primary'>排序</Button>
            </div>
        </div>
    )
})


export default inject((stores) => ({ TableStore: stores.TableStore }))(SortLayout);
