/*
 * @Author: your name
 * @Date: 2022-04-02 11:12:08
 * @LastEditTime: 2022-04-05 20:40:03
 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\layouts\BasicManage\ComputerPage\index.js
 */

import { inject, observer } from 'mobx-react'
import React, { Component } from 'react'
import { Table } from 'antd'
import GlobalForm from 'components/GlobalForm';
import { MenuObj } from 'constants/configs';
import { checkCurrentMenu } from 'utils/dataTools';
import { toJS } from 'mobx';


@inject('BasicStore')
@inject('HomeStore')
@observer
class ComputerPage extends React.Component {
  render() {
    const { data } = this.props.BasicStore;
    const { model } = this.props.HomeStore;
    const columns = []
    const dataSource = [];
    for (var i = 0; i < data[0].data.length; i++) {
      var obj = {}
      obj.title = data[0].data[0].label
      obj.dataIndex = data[0].data[0].label
      obj.key = data[0].data[0].label
      columns.push(obj)
    }
    for (var i = 0; i < data.length; i++) {
      var obj = {}
      obj.key = (i + 1).toString()
      for (var j = 0; j < data[i].data.length; j++) {
        const label = data[i].data[j].label
        console.log(data[i].data[j].attr.value);
        obj[label] = data[i].data[j].attr.value
      }
      dataSource.push(obj)
    }
    console.log(columns);
    console.log(dataSource);
    if (model == 'look') {
      return <Table columns={columns} dataSource={dataSource}></Table>;
    } else {
      return <GlobalForm />

    }


  }

  componentDidMount(){
    let actionId = selectedKeys[0];
    let menuObj = toJS(MenuObj.leafMenuModels);
    let current = checkCurrentMenu({ menuObj, actionId });
    let params = {};
    params.formId = current.currentMenu[0].id.toString();
    this.props.BasicStore.queryAll(params);
  }
}

export default ComputerPage;
