import React from 'react'
import { inject,observer } from 'mobx-react'
import { toJS } from 'mobx'
import { Tabs,Empty } from 'antd'
import ListPage from './ListPage'
import HistoryPage from './HistoryPage'
import OperatePage from './OperatePage'
import WorkDaily from './WorkDaily';
import { isEmpty } from 'lodash';
import { work_ChildrenMenu } from 'constants/configs';
import './index.less'
import { initSearchQuery } from 'utils/dataTools';

const { TabPane } = Tabs;
@inject('WorkStore','DeviceDefineStore')
@observer
class WorkPage extends React.Component{
  constructor(props){
    super(props)
    this.store = this.props.WorkStore;
    this.state = {
      workTabList:[]
    }
  }
  render(){
    const { current,workerAuth } = toJS(this.store);
    return <div className='define-container'>
      {
        !workerAuth.admin && !workerAuth.repair ? <Empty
          style={{marginTop:'100px'}}
          image="https://gw.alipayobjects.com/mdn/miniapp_social/afts/img/A*pevERLJC9v0AAAAAAAAAAABjAQAAAQ/original"
          imageStyle={{
            height: 100,
          }}
          description={
            <span>
              您没有当前菜单的权限，请联系系统管理员申请。
            </span>
          }

        /> : (!this.store.isDaily ? <React.Fragment>

          <Tabs activeKey={`${current}`} onChange={this.onStepChange}>
             <TabPane tab={<div><i className="iconfont icon-task1 icon-spacing"></i>待办任务</div>} key="1" />
             <TabPane tab={<div><i className="iconfont icon-mission icon-spacing"></i>工单明细</div>} key="2" />
             <TabPane tab={<div><i className="iconfont icon-list icon-spacing"></i>工单报告</div>} key="3" />
          </Tabs>

           {/* 
          <Tabs activeKey={`${current}`} onChange={this.onStepChange}>
            {
              this.state.workTabList.map(item=>{
                return <TabPane key={item.id}  tab={item.name}>

                </TabPane>
              })
            }
          </Tabs>
          */}
          <div className="tab-content-bg">
          { current === '1' ? <OperatePage /> : current === '2' ? <ListPage /> : <HistoryPage /> }
          </div>
        </React.Fragment> : <WorkDaily />)
      }
    </div>;
  }
  onStepChange = (step) => {
    this.store.current = step;
    this.store.searchCondition = {}
    this.props.history.push({search:'?tab='+step})
    this.store.getWork({})
  }
  componentWillUnmount(){
    this.store.current = '1';
    this.store.isDaily = false;
  }
  componentDidMount(){
    let query = {}
    if(this.props.location.search){
      query = initSearchQuery(this.props.location.search)
    }
    this.store.getWorkAuth().then(()=>{
      const workTabList = !isEmpty(this.store.workerAuth) ? (this.store.workerAuth.admin ? work_ChildrenMenu : work_ChildrenMenu.splice(1)) : work_ChildrenMenu;
      this.setState({
        workTabList
      })
      if(this.store.workerAuth.admin){
        this.store.current = '1'
      }else{
        this.store.current = '2'
      }
      if(this.props.location.search){
        this.store.current = query.tab;
        if(query.workNo){
          this.store.searchCondition = {
            workNo : query.workNo
          };
        }
      }
      if(query.workNo){
        this.props.history.push({search:'?tab='+this.store.current+'&workNo='+query.workNo})
        this.store.getWork({workNo:query.workNo})
      }else{
        this.props.history.push({search:'?tab='+this.store.current})
        this.store.getWork({});
      }
    });
    this.props.DeviceDefineStore.getSelectType()
		this.props.DeviceDefineStore.getUsersByRoles()
  }

}

export default WorkPage
