/*
 * @Author: your name
 * @Date: 2021-11-02 14:29:58
 * @LastEditTime: 2022-03-23 08:22:52
 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage\src\routes\HomeRouter\index.js
 */
/**
 * 最外层包裹的home组件，包括
 */
import React, { PureComponent } from 'react';
import HomeLayout from 'Layouts/HomeLayout';
import {
  Route, Redirect,
} from 'react-router-dom';
import {
  HomePage,
  BasicManage, WorkManage, SystemManage,
  SparepartsManage, StatisticsManage,DeviceManage,
  EditPage
} from './configs'

class HomeRouter extends PureComponent {
  state = {
    innerHeight: window.innerHeight
  };
  render() {
    const isMobile = navigator.userAgent.toLowerCase().indexOf('mobile') > -1 ? 'mobile' : 'pc';
    return <div className='main'
      style={{
        height: this.state.innerHeight + 'px',
      }}
    >
      <HomeLayout type={isMobile} children={<React.Fragment>
        <Route exact path="/" render={() => <Redirect to='/login' />} />
        <Route path='/index' component={HomePage} />
        <Route path='/device' component={DeviceManage} />
        <Route path='/work' component={WorkManage} />
        <Route path='/spareparts' component={SparepartsManage} />
        <Route path='/statistics' component={StatisticsManage} />
        <Route path='/basic' component={BasicManage} />
        <Route path='/system' component={SystemManage} /> 
        <Route path='/editPage' component={EditPage} />

      </React.Fragment>} />
    </div>;
  }
  componentDidMount() {
    window.addEventListener('resize', this.handleResize.bind(this));
  }
  handleResize = (e) => {
    this.setState({
      innerHeight: e.target.innerHeight
    });
  }

}
export default HomeRouter;
