import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { toJS } from 'mobx';
import { Form } from '@ant-design/compatible';
import '@ant-design/compatible/assets/index.css';
import { message, Input, Radio, Select } from 'antd';
import GlobalModal from 'Components/GlobalModal';
@inject('SystemStore')
@observer
class MenuDialog extends Component {
    render() {
        const { modalVisible, formData, arr } = toJS(this.props.SystemStore)
        const formLabelCol = { span: 4 }
        const formWrapperCol = { span: 18 }
        const { Option } = Select;
        const { getFieldDecorator } = this.props.form;
        return <div>
            {
                modalVisible && <div>
                    <GlobalModal
                        title={formData.menuId ? '编辑' : '新增'}
                        visible={modalVisible}
                        onOk={this.handleSubmit}
                        width={'50%'}
                        onCancel={e => this.props.SystemStore.modalChange(0)}
                        children={
                            <div>
                                <Form onSubmit={this.handleSubmit} layout='horizontal' labelCol={formLabelCol} wrapperCol={formWrapperCol}>
                                    <Form.Item label="上级菜单">
                                        {getFieldDecorator('parentId', {
                                            initialValue: formData.parentId || '0'
                                        })(
                                            <Select disabled>
                                                <Option value='0'>根目录</Option>
                                                {
                                                    arr.map((item, index) => (
                                                        <Option key={index} value={item.menuId}>{item.name}</Option>
                                                    ))
                                                }
                                            </Select>
                                        )}
                                    </Form.Item>
                                    <Form.Item label="菜单类型">
                                        {getFieldDecorator('type', {
                                            rules: [{ required: true, message: '请选择菜单类型' }],
                                            initialValue: formData.type || ''
                                        })(
                                            <Radio.Group>
                                                <Radio value={0}>目录</Radio>
                                                <Radio value={1}>菜单</Radio>
                                            </Radio.Group>
                                        )}
                                    </Form.Item>
                                    <Form.Item label="菜单名称">
                                        {getFieldDecorator('name', {
                                            rules: [{ required: true, message: '请输入菜单名称' }],
                                            initialValue: formData.name || ''
                                        })(
                                            <Input placeholder="请输入菜单名称" />
                                        )}
                                    </Form.Item>
                                    <Form.Item label="链接地址">
                                        {getFieldDecorator('url', {
                                            rules: [{ required: false, message: '请输入链接地址' }],
                                            initialValue: formData.url || ''
                                        })(
                                            <Input placeholder="请输入链接地址" />
                                        )}
                                    </Form.Item>
                                    <Form.Item label="权限标识">
                                        {getFieldDecorator('perms', {
                                            rules: [{ required: false, message: '请输入权限标识' }],
                                            initialValue: formData.perms || ''
                                        })(
                                            <Input placeholder="请输入权限标识" />
                                        )}
                                    </Form.Item>
                                    <Form.Item label="排序号">
                                        {getFieldDecorator('orderNum', {
                                            rules: [{ required: false, message: '请输入排序号' }],
                                            initialValue: formData.orderNum || ''
                                        })(
                                            <Input placeholder="请输入排序号" />
                                        )}
                                    </Form.Item>

                                    <Form.Item label="图标">
                                        {getFieldDecorator('icon', {
                                            rules: [{ required: false, message: '请输入图标' }],
                                            initialValue: formData.icon || ''
                                        })(
                                            <Input placeholder="请输入图标" />
                                        )}
                                    </Form.Item>
                                </Form>
                            </div>
                        }
                    />

                    {/*innerModalVisible && <GlobalModal
                        title='选择图标'
                        visible={innerModalVisible}
                        onOk={this.handleSubmit}
                        width={'50%'}
                        onCancel={e => this.props.SystemStore.modalChange(0)}
                        children={
                            <div></div>
                        } />*/}
                </div>
            }
        </div>;
    }
    //提交表单
    handleSubmit = e => {
        e.preventDefault();
        const { getMenuData, modalChange, addNewMenu, formData, } = this.props.SystemStore
        this.props.form.validateFields((err, values) => {
            if (!err) {
                if (formData.id) {
                    addNewMenu(0, { ...formData, ...values }).then(res => {
                        message.success('更新成功')
                        modalChange(0)
                        getMenuData()
                    })
                } else {
                    values.parentId = values.parentId === '根目录' ? 0 : values.parentId
                    addNewMenu(1, values).then(res => {
                        message.success('添加成功')
                        modalChange(0)
                        getMenuData()
                    })
                }
            }
        });
    };
}
const MenuDialogForm = Form.create({ name: 'define' })(MenuDialog);
export default MenuDialogForm;
