import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import './index.less';
import GlobalList from 'components/GlobalList';
import { Select, } from 'antd';
import { setQueryColumns, Query_Result } from './methods';

const { Option } = Select;

@inject('ZCloudDeviceStore')
@observer
class CheckServer extends Component {
    constructor(props) {
        super(props);
        this.store = this.props.ZCloudDeviceStore;
        this.state = {
            selectedRowKeys: [],
            visible: false,
            contentType: 1,
            maxValue: '',
            minValue: '',
            extendFormColumns: []
        };
    }
    render() {
        const {
            queryList,
            queryPage,
            isLoading,
            deviceByIdList,
            deviceList,
        } = this.store;
        const rowSelection = {
            selectedRowKeys: this.state.selectedRowKeys,
            onChange: this.handleSelect
        };
        return (
            <div>
                <div>
                    <Select
                        showSearch allowClear
                        style={{ width: 250, }}
                        placeholder='设备类型'
                        onChange={this.onSelect.bind(this, 'typeId')}
                    >
                        {
                            deviceList && deviceList.map(item => {
                                return <Option key={item.typeId} value={item.typeId}>
                                    {item.typeId}_{item.typeName}
                                </Option>
                            })
                        }
                    </Select>
                    <Select
                        showSearch allowClear
                        style={{ width: 250, margin: '0 10px' }}
                        placeholder='选择设备'
                        onChange={this.onSelect.bind(this, 'deviceId')}
                    >
                        {
                            deviceByIdList.length > 0 && deviceByIdList.map(item => {
                                return <Option key={item.id} value={item.id}>
                                    {item.devName}
                                </Option>
                            })
                        }
                    </Select>
                    <Select
                        showSearch allowClear
                        style={{ width: 250, }}
                        placeholder='点检结果'
                        onChange={this.onSelect.bind(this, 'result')}
                    >
                        <Option value="">全部</Option>
                        {
                            Object.keys(Query_Result).map(item => {
                                return <Option key={item} value={item}>
                                    {Query_Result[item]}
                                </Option>
                            })
                        }
                    </Select>
                </div>
                <GlobalList
                    table={{
                        rowKey: 'id',
                        columns: setQueryColumns(this.props.type),
                        dataSource: queryList,
                        pagination: {
                            ...queryPage,
                            onChange: (pageIndex, pageSize) => {
                                this.store.getQuery({
                                    pageIndex,
                                    pageSize
                                });
                            }
                        },
                        isLoading,
                        rowSelection,
                        // btns: [
                        //     {
                        //         name: '导出Excel',
                        //         onClick: () => {
                        //             this.handleVisible();
                        //         },
                        //         type: 'primary'
                        //     }
                        // ]
                    }}
                    modal={{
                        children: <span>导出</span>
                    }}
                />
            </div>
        );
    }
    onSelect = (type, value) => {
        this.store[type] = value;
        if (type === 'typeId') {
            this.store.getListByType('device', { typeId: value });
        }
        this.store.getQuery()
    }
    handleVisible = (status) => {
        this.setState({
            visible: !this.state.visible
        });
    };
    handleSelect = selectedRowKeys => {
        this.setState({ selectedRowKeys });
    };
    componentDidMount() {
        this.store.getQuery();
    }
    componentWillUnmount() {
        this.store.clearData()
    }
}
export default CheckServer;
