import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import './index.less';
import { Table, Tabs } from 'antd';
// import TableLayout from 'Components/TableLayout'
// import { toJS } from 'mobx';



@inject('SparepartsStore')
@observer


class DepartmentTable extends Component{
  render(){
    return <div className="tab-content-bg content">
      <div className="content-box">
          <div className="state">未派单</div>
          <div className="data">
            <p>201906190900 由某公司某人上报某型号某名称设备的维修请求需进行维修派单操作</p>
            <p className="p-border">201906190900 某客户的某型号设备需进行保养派单操作</p>
          </div>
          <div className="clear"></div>
      </div>
      <div className="content-box">
          <div className="state">待响应</div>
          <div className="data">
            <p>你有一条新的 (维修/保养) 工单需响应</p>
          </div>
          <div className="clear"></div>
      </div>
      <div className="content-box">
          <div className="state">已响应</div>
          <div className="data">
            <p>(维修/保养) 工单 201906190900 由 XXX 在 XX时间 已响应</p>
          </div>
          <div className="clear"></div>
    </div>
    <div className="content-box">
          <div className="state">进行中</div>
          <div className="data">
            <p>(维修/保养) 工单 201906190900 由 XXX 在 XX时间 更新状态为进行中</p>
          </div>
          <div className="clear"></div>
    </div>
    <div className="content-box">
          <div className="state">已响应</div>
          <div className="data">
            <p>(维修/保养) 工单 201906190900 由 XX 更新状态为已完成</p>
          </div>
          <div className="clear"></div>
    </div>
    <div className="content-box">
          <div className="state">未完成</div>
          <div className="data">
            <p>(维修/保养) 工单 201906190900 由 XX 更新状态为未完成</p>
            <p className="p-border">某型号的某设备超时未(点检/巡检)</p>
          </div>
          <div className="clear"></div>
    </div>
    <div className="content-box">
          <div className="state">已结单</div>
          <div className="data">
            <p>你有一条 (维修/保养) 工单 201906190900 已结单</p>
          </div>
          <div className="clear"></div>
    </div>
    </div>
  }
}
export default DepartmentTable;



