/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-01 20:45:23
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-18 23:58:43
 * @FilePath: \bl-device-manage-test\src\layouts\FormEdit\index.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { BulbTwoTone, CloseOutlined } from '@ant-design/icons';
import { Drawer, message, Tabs } from 'antd';
import FormRender, { useForm } from 'form-render';
import { defaultCommonSetting, defaultGlobalSettings, defaultSettings } from 'constants/field_config';
import Generator from 'fr-generator';
import { inject, observer } from 'mobx-react';
import React, { useEffect, useRef, useState } from 'react';
import { Link_item } from './self_item/link_item';
import { toJS } from 'mobx';
import './index.css'
import { exChange, restore } from './changeTool';
import { Submit_check } from './self_item/submit_check';
import { Self_divider } from './self_item/self_divider';
import { MyMulTag } from './self_item/myMulTag';
import mul_tag from './self_item/mul_tag';

const { Provider, Sidebar, Canvas, Settings } = Generator;


const FormEdit = observer(({ HomeStore, FormStore }) => {
  const [schema, setSchema] = useState({});
  const [visible, setVisisble] = useState(false)
  const [lookItem, setLookItem] = useState({})
  const [tagLayout, setTagLayout] = useState("")
  const ref = useRef();
  const ref1 = useRef()
  const ref2 = useRef()
  const ref3 = useRef()
  const form = useForm();
  useEffect(() => {
    FormStore.getFormField({ formId: HomeStore.firstFormId }).then(() => {
      console.log(toJS(FormStore.formField));
      console.log(restore(toJS(FormStore.formField)));
      setSchema(restore(toJS(FormStore.formField)))
    });
    console.log(111);
  }, []);
  const look = () => {
    setLookItem(ref.current.getValue())
    setVisisble(true);
  }
  const save = () => {
    console.log(ref.current.getValue());
    let params = exChange(ref.current.getValue(), HomeStore.firstFormId,'root')
    let obj1 = exChange(ref1.current.getValue(), HomeStore.firstFormId,'title1')
    let obj2 = exChange(ref2.current.getValue(), HomeStore.firstFormId,'title2');
    params['subForms'].push(obj1['subForms'][0])
    params['subForms'].push(obj2['subForms'][0])
    // FormStore.saveForm(params).then(() => {
    //   message.success('保存成功')
    // })
    console.log(params);
    // console.log(ref3.current.getValue() || undefined);
  }
  const closeDrawer = () => {
    setVisisble(false)
  }

  const getTag = () => {
    console.log(ref.current);
    if (ref.current && ref.current.getValue().hasOwnProperty('mul_tag') && ref.current.getValue()['mul_tag']) {
      console.log(ref.current.getValue()['mul_tag']);
      ref.current.getValue()['mul_tag'].map((item, index) => {
        return (
          <Tabs.TabPane tab={item['title']} key={item['title']}>
            <Provider
              extraButtons={[false, false, false, false]}
              ref={`ref${index}`}
              onSchemaChange={(schema) => console.log(schema)}
            >
              <Canvas />
            </Provider>
          </Tabs.TabPane>
        )
      })
    }
  }
  return (
    <>
      <div className='edit_header'>
        <div className='edit_left'>
          <BulbTwoTone className='icon_edit' />
          <a>查看新手引导</a>
        </div>
        <div className='edit_right'>
          <button className='edit_look' onClick={look}>预览</button>
          <button className='edit_save' onClick={save}>保存</button>
        </div>
      </div>
      <div className="fr-generator-playground" style={{ height: '88vh' }}>
        <Provider
          ref={ref}
          defaultValue={schema['root']}
          settings={defaultSettings}
          commonSettings={defaultCommonSetting}
          globalSettings={defaultGlobalSettings}
          extraButtons={[false, false, false, false]}
          controlButtons={[true, false, { text: 'up', onClick: (event, schema) => console.log(event, schema) }]}
          hideId={true}
          onSchemaChange={(schema) => console.log(ref.current.getValue())}
          widgets={{
            link_item: Link_item,
            submit_check: Submit_check,
            self_divider: Self_divider,
            mul_tag: mul_tag
          }}
        >
          <div className="fr-generator-container" style={{ height: '88vh' }}>
            <Sidebar />
            <div style={{ display: 'flex', flexDirection: 'column' ,width:'100%'}}>
              <Canvas />
              <Tabs>
                <Tabs.TabPane tab="{item['title']}" key="{item['title1']}">
                  <Provider
                    extraButtons={[false, false, false, false]}
                    ref={ref1}
                    onSchemaChange={(schema) => console.log(schema)}
                  >
                    <Canvas />
                  </Provider>
                </Tabs.TabPane>
                <Tabs.TabPane tab="{item['title']}" key="{item['title2']}">
                  <Provider
                    extraButtons={[false, false, false, false]}
                    ref={ref2}
                    onSchemaChange={(schema) => console.log(schema)}
                  >
                    <Canvas />
                  </Provider>
                </Tabs.TabPane>
              </Tabs>
            </div>
            <Settings />
          </div>
        </Provider>
      </div>

      <Drawer
        placement='bottom'
        size='large'
        width={1200}
        visible={visible}
        extra={
          <CloseOutlined onClick={closeDrawer} />
        }
      >
        <FormRender schema={lookItem} form={form} />
      </Drawer>
    </>
  );
});

export default inject((stores) => ({ HomeStore: stores.HomeStore, FormStore: stores.FormStore }))(FormEdit);
