/*
 * @Author: your name
 * @Date: 2022-01-24 16:24:19
 * @LastEditTime: 2022-07-07 14:58:47
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage\src\layouts\LoginPage\index.js
 */
import React from 'react';
import { Input, Checkbox, Button, Form, Dropdown } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import { withRouter } from 'react-router-dom';
import { inject, observer } from 'mobx-react';
import { setCookie, clearCookie } from 'utils/dataTools';
import './index.less';

@withRouter
@inject('HomeStore')
@observer
class LoginPage extends React.Component {
  render() {
    return (
      <div className='login_main'>
        <div className='login_header'>
          <div className='header_left'>
            <div className='header_left_item'>产品</div>
            <div className='header_left_item'>方案</div>
            <div className='header_left_item'>案例</div>
            <div className='header_left_item'>定价</div>
            <div className='header_left_item'>资源中心</div>
            <div className='header_left_item'>零代码</div>
          </div>
          <div className='header_right'>
            <div className='header_left_item'>
              <Button>登录</Button>
            </div>
            <div className='header_left_item'>
              <Button>注册</Button>
            </div>
            <div className='header_left_item'>
              简体中文
            </div>
          </div>
        </div>
        <div className='shadow'></div>
        <div className='login_content'>
          <div className='content_first'>
            <div className='first_left'>
              <div className='first_lineO'>解决设备全生命周期管理难题</div>
              <div className='first_lineT'>一张二维码就能搞定设备管理？手机扫描设备二维码，
                <br />即可解决设备档案管理、巡检、报修、保养、分析预警等问题
              </div>
              <Button className='first_btn'>点击预览方案</Button>
              <div className='first_lineTh'>* 初次注册并安装应用者，默认为管理员</div>
            </div>
            <div className='first_right'>

            </div>
          </div>
        </div>
        <div className='login_footer'></div>
      </div>);
  }
  handleSubmit = values => {
    if (values.remember) {
      for (let name in values) {
        setCookie(name, values[name]);
      }
    } else {
      for (let name in values) {
        clearCookie(name);
      }
    }
    // 请求登陆接口
    this.props.HomeStore.setLogin({
      grant_type: 'password',
      username: values.username,
      password: values.password,
      client_id: 'pc',
      client_secret: 'secret',
    }, (result) => {
      const { success, token } = result;
      if (success) {
        sessionStorage.setItem('selfToken', 'Bearer ' + token); // 设置前局登陆token
        sessionStorage.setItem('username', values.username); // 设置登录用户名
        this.props.history.push({
          pathname: '/form',
          state: { username: values.username, password: values.password, selfToken: token }
        });
      }
    })
  };
}
export default LoginPage;
