import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { toJS } from 'mobx';
import { message, Form, Input, Select } from 'antd';
import GlobalModal from 'Components/GlobalModal';
@inject('SparepartsStore')
@observer
class SparepartsDialog extends Component {
    constructor(props) {
        super(props)
        this.state = {
            val: 1
        }
    }
    render() {
        const { modalVisible, formData, workList, modalTitle, spareList } = toJS(this.props.SparepartsStore)
        const formLabelCol = { span: 4 }
        const formWrapperCol = { span: 18 }
        const { Option } = Select
        const { getFieldDecorator } = this.props.form;
        return <div>
            {
                modalVisible && <GlobalModal
                    title={modalTitle === 1 ? '入库' : modalTitle === 2 ? '出库' : '还库'}
                    visible={modalVisible}
                    onOk={this.handleSubmit}
                    onCancel={e => this.props.SparepartsStore.modalChange(0)}
                    children={
                        <div>
                            <Form onSubmit={this.handleSubmit} layout='horizontal' labelCol={formLabelCol} wrapperCol={formWrapperCol}>
                                <Form.Item label="备件名称">
                                    {getFieldDecorator('spareId', {
                                        rules: [{ required: true, message: '请选择备件名称' }],
                                        initialValue: formData.id || ''
                                    })(
                                        <Select placeholder='请选择备件名称' onChange={this.chooseSpa}>
                                            {spareList.map(item => <Option key={item.id} value={item.id}>{item.spareName}</Option>)}
                                        </Select>
                                    )}
                                </Form.Item>
                                {(modalTitle !== 1 && formData.spareId) && <Form.Item label="关联工单">
                                    {getFieldDecorator('workId', {
                                        rules: [{ required: true, message: '请选择关联工单' }],
                                        initialValue: formData.warehouseName || ''
                                    })(
                                        <Select placeholder='请选择关联工单'>
                                            {workList.map(item => (<Option value={item.id}>{item.workNo}</Option>))}
                                        </Select>
                                    )}
                                </Form.Item>}
                                <Form.Item label="数量">
                                    {getFieldDecorator('number', {
                                        rules: [{ required: true, message: '请输入数量' }],
                                        initialValue: formData.description || ''
                                    })(
                                        <Input placeholder="请输入数量" />
                                    )}
                                </Form.Item>
                                <Form.Item label="备注">
                                    {getFieldDecorator('remark', {
                                        rules: [{ required: false, message: '请选择等级' }],
                                        initialValue: formData.remark || ''
                                    })(
                                        <Input placeholder="请输入备注" />
                                    )}
                                </Form.Item>
                            </Form>
                        </div>
                    }
                />
            }
        </div>;
    }
    chooseSpa = (e) => {
        if (this.props.SparepartsStore.modalTitle !== 1) {
            this.props.SparepartsStore.getworkData({ spareId: e, type: this.props.SparepartsStore.modalTitle })
        }
        this.props.SparepartsStore.formData.spareId = e

    }
    setValue = (e) => {
        this.setState({
            val: e.target.value
        })
    }
    //提交表单
    handleSubmit = e => {
        e.preventDefault();
        const { getData, modalChange, inPutStorage, formData, PageInfo, modalTitle, spareName } = this.props.SparepartsStore
        this.props.form.validateFields((err, values) => {
            if (!err) {
                inPutStorage({ ...formData, ...values, type: modalTitle }).then(res => {
                    if (res) {
                        message.success('操作成功')
                        modalChange(0)
                        getData(2, { spareName, pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize })
                    }
                })
            }
        });
    };
    componentDidMount() {
        // this.props.SparepartsStore.getProList()
    }
}
const SparepartsDialogForm = Form.create({ name: 'define' })(SparepartsDialog);
export default SparepartsDialogForm;
