/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-02 03:21:54
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-02 14:35:23
 * @FilePath: \bl-device-manage-test\src\layouts\FormLayout\index.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import React from 'react';
import FormRender, { useForm } from 'form-render';

const schema = {
  "type": "object",
  "properties": {
    "input_TPe1kC": {
      "title": "单行文本",
      "type": "string",
      "check": {}
    },
    "textarea_xfZByD": {
      "title": "多行文本",
      "type": "string",
      "format": "textarea",
      "check": {}
    },
    "number_-zBH2i": {
      "title": "数字",
      "type": "number"
    }
  },
  "labelWidth": 120,
  "displayType": "row"
};
const onFinish = (formData) => {
  console.log('formData', formData);
}
const FormLayout = () => {
  const form = useForm();

  return (
    <>
      <FormRender schema={schema} form={form} onFinish={onFinish} />
      <Button onClick={form.submit} type="primary">提交</Button>
    </>
  );
};

export default FormLayout;
