import { observable, action, } from 'mobx';
import { isDataExist } from 'Utils/dataTools';
import * as services from '../services/basic';
import { message } from 'antd';

class TitleConfig {
  @observable titleList = []
  @observable isLoading = false
  @action.bound async getTitle(params){
    this.isLoading = true
    this.titleList = []
    try {
      let res = await services.gets('getTitle')(params)
      this.isLoading = false
      if(isDataExist(res)){
        this.titleList = res.data.data;
      }
    } catch (error) {
      console.log(error)
    }
  }
  @action.bound async saveTitle(params,extParams){
    this.isLoading = true
    try {
      let res = await services.posts('saveTitle')(params)
      this.isLoading = false
      if(isDataExist(res)){
        message.success('保存成功！')
        this.getTitle({...extParams})
      }
    } catch (error) {
      console.log(error)
    }
  }

}
let TitleConfigStore = new TitleConfig();
export default TitleConfigStore;
