import React,{ Component } from 'react';
import { Input, Form, Button, DatePicker,Select, Modal } from 'antd';
import { inject,observer } from 'mobx-react';
import { toJS } from 'mobx';
const { Option } = Select;

const formItemLayout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 12 },
};
const { moment } = window.GLOBAL_PARAMS;

@inject('DeviceDefineStore')
@observer
class AddModal extends Component{
  constructor(props){
    super(props)
    this.state = {
      value:'',
      radio:1,
      showMore : false
    }
    this.store = this.props.DeviceDefineStore;
  }
  render(){
    const { getFieldDecorator } = this.props.form;
    const { status } = this.props;
    const { listVisible,devCurrent,selectModel,selectType,employeeList } = toJS(this.store);
    return <Modal
      // width = {950}
      title={`${status === 'query' ? '查看' : status === 'add' ? "新增" : '编辑'}`}
      visible={listVisible}
      onCancel={this.props.onClose}
      footer={
        status !== 'query' ? <div>
          <Button onClick={this.props.onClose}>取消</Button>
          <Button type='primary' onClick={this.onOk}>确定</Button>
        </div> : <Button type='primary' onClick={this.props.onClose}>关闭</Button>
      }
    >
      <Form>
        <h4>基本信息</h4>
        <Form.Item {...formItemLayout} label="设备名称">
          {getFieldDecorator('deviceName', {
            initialValue:status !== 'add' ? devCurrent.deviceName : undefined,
            rules: [
              {
                required: true,
                message: '请填写设备名称',
              },
            ],
          })(
            <Input disabled={status === 'query'} placeholder='请输入' style={{width:'200px'}} />
          )}
        </Form.Item>
        <Form.Item {...formItemLayout} label="设备编号">
          {getFieldDecorator('deviceCode', {
            initialValue:status !== 'add' ? devCurrent.deviceCode : undefined,
            rules: [
              {
                required: true,
                message: '请填写设备编号',
              },
            ],
          })(
            <Input disabled={status === 'query'} placeholder='请输入' style={{width:'200px'}} />
          )}
        </Form.Item>
        <Form.Item {...formItemLayout} label="设备类型">
          {getFieldDecorator('typeId', {
            initialValue: status !== 'add' ? devCurrent.typeId : undefined,
            rules: [{ required: true, message: '请选择设备类型' }]
          })(
            <Select 
              showSearch
              optionFilterProp='children'
              placeholder="请选择"
              style={{ width: 200 }} 
              onChange={this.selectTypeChange}
            > 
              {
                selectType.length > 0 && selectType.map(mas =><Option key={mas.id} value={mas.id}>{mas.typeName}</Option>)
              }
            </Select>
          )}
        </Form.Item>
        <Form.Item {...formItemLayout} label="设备型号">
          {getFieldDecorator('modelId', {
            initialValue: status !== 'add' ? devCurrent.modelId : undefined,
            rules: [{ required: true, message: '请选择设备型号' }]
          })(
            <Select 
              showSearch
              optionFilterProp='children'
              placeholder="请选择"
              style={{ width: 200 }} 
            > 
              {
                selectModel.length > 0 && selectModel.map(mas =><Option key={mas.id} value={mas.id}>{mas.modelName}</Option>)
              }
            </Select>
          )}
        </Form.Item>
        <Form.Item {...formItemLayout} label="创建时间">
          {getFieldDecorator('deviceTime', {
            initialValue: status !== 'add' ? moment(devCurrent.deviceTime) : moment(new Date()) ,
            // rules: [{ type: 'object', required: true, message: '请选择时间!' }]
          })(
            <DatePicker 
              showTime 
              format="YYYY-MM-DD HH:MM:SS" 
              locale={window.GLOBAL_PARAMS.locale} 
              placeholder="请选择日期" 
              style={{ width: '200px',display:'inline-block' }} 
            />
          )}
        </Form.Item>
        <Form.Item {...formItemLayout} label="保修期（天）">
          {getFieldDecorator('guaranteePeriod', {
            initialValue: status !== 'add' ? devCurrent.guaranteePeriod : undefined,

          })(
            <Input disabled={status === 'query'} placeholder='请输入' style={{width:'200px'}} />
          )}
        </Form.Item>
        {/* <Form.Item {...formItemLayout} label="计量单位">
          {getFieldDecorator('unit', {
            initialValue:status !== 'add' ? devCurrent.unit : undefined,
            rules: [
              {
                required: true,
                message: '请填写计量单位',
              },
            ],
          })(
            <Input disabled={status === 'query'} placeholder='请输入' style={{width:'200px'}} />
          )}
        </Form.Item> */}
        <Form.Item {...formItemLayout} label="点检人员">
          {getFieldDecorator('checkId', {
            initialValue:status !== 'add' ? devCurrent.checkId : undefined,
          })(
            <Select 
              showSearch
              optionFilterProp='children'
              placeholder="请选择"
              style={{ width: 200 }} 
            > 
              {
                employeeList.length > 0 && employeeList.map(mas =><Option key={mas.id} value={mas.id}>{mas.name}</Option>)
              }
            </Select>
          )}
        </Form.Item>
        <Form.Item {...formItemLayout} label="巡检人员">
          {getFieldDecorator('inspectionId', {
            initialValue:status !== 'add' ? devCurrent.inspectionId : undefined,
          })(
            <Select 
              showSearch
              optionFilterProp='children'
              placeholder="请选择"
              style={{ width: 200 }} 
            > 
              {
                employeeList.length > 0 && employeeList.map(mas =><Option key={mas.id} value={mas.id}>{mas.name}</Option>)
              }
            </Select>
          )}
        </Form.Item>
        <Form.Item {...formItemLayout} label="维修人员">
          {getFieldDecorator('repairId', {
            initialValue:status !== 'add' ? devCurrent.repairId : undefined,
          })(
            <Select 
              showSearch
              optionFilterProp='children'
              placeholder="请选择"
              style={{ width: 200 }} 
            > 
              {
