/*
 * @Author: your name
 * @Date: 2022-03-31 23:08:16
 * @LastEditTime: 2022-04-07 15:29:59
 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\stores\SpareStore
 */
import { observable, action } from 'mobx';
import { isDataExist } from '../utils/dataTools';
import * as services from '../services/basic';

class Basic {
    @observable columns = [];
    @observable dataSource = [];
    @observable PageInfo = {pageIndex: 1, pageSize: 2};

    @action.bound async queryAll(params) {
        try {
            // let res = await services.getRequest(services.requestList.getFieldNameAndType, params);
            let res = {}
            res.data = {
                "firstFormId":"1",
                "secondFormId":"0",
            
                "columns":[
                    {
                        "id":"tenement_1_form1_1_form2_0_id_1",
                        "typeId":"1",
                        "name":"车间",
                        "updateTime":"",
                        "order":"1",
                        "lastModifyPeopleNickName":"",
                    
                        "others":{
                            // 自由发挥  ........
                        }
                    }
                ],
            
                "dataSource":[
                    {
                        "dataId":"1",
                        "tenementId":"1",
                        "secondFormId":"0",
                        "data":{
                            "tenement_1_form1_1_form2_0_id_1":"车间1",
                        },
                        "lastModifyPeopleNickName":"",
                        "updateTime":"",
                        "isDelete": false,
                        "others":{
                            // 其余自由发挥  ........
                        }
                    }
                ]
               }
            res.code = 0
            if (isDataExist(res)) {
                const dataColumns = res.data.columns
                const dataDataSource = res.data.dataSource
                const columns = [];
                const dataSource = [];
                for (var i = 0; i < dataColumns.length; i++) {
                    var obj = {}
                    obj.title = dataColumns[i].name
                    obj.dataIndex = dataColumns[i].id
                    obj.key = dataColumns[i].id
                    columns.push(obj)
                }
                this.columns = columns
                for (var i = 0; i < dataDataSource.length; i++) {
                    var obj = {}
                    obj = dataDataSource[i].data;
                    obj.id = dataDataSource[i].dataId
                    obj.key = (i + 1).toString()
                    
                    dataSource.push(obj)
                }
                this.dataSource = dataSource;
            }
        } catch (error) {
            console.log(error);
        }
    }


    @action.bound async addNew(params) {
        try {
            let res = await services.putRequest(services.requestList.saveDatabase, params);
            console.log(params);
            if (isDataExist(res)) {
                return res;
            }
        } catch (error) {
            console.log(error);
        }
    }
}

let BasicStore = new Basic();
export default BasicStore;
