import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Divider, Modal, message, Badge, Form, Input } from 'antd';
import TableLayout from 'Components/TableLayout'
import GlobalModal from 'Components/GlobalModal';
import { toJS } from 'mobx';


@inject('SystemStore')
@observer
class UserTable extends Component {
    constructor(props) {
        super(props)
        this.state = {
            visible: false
        }
    }
    render() {
        const { tableData, PageInfo, isLoading, } = toJS(this.props.SystemStore)
        //定义表头
        const columns = [
            {
                title: '姓名',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: '用户名/工号',
                dataIndex: 'username',
                key: 'username',
            },
            {
                title: '手机',
                dataIndex: 'mobile',
                key: 'mobile',
                render: (text) => (
                    <div>{text ? text : '-'}</div>
                )
            },
            {
                title: '邮箱',
                dataIndex: 'email',
                key: 'email',
                render: (text) => (
                    <div>{text ? text : '-'}</div>
                )
            },
            {
                title: '状态',
                dataIndex: 'status',
                key: 'status',
                render: (text, record) => (
                    <div>
                        {text ? <Badge status='success' text="正常" /> : <Badge status='error' text="禁用" />}
                    </div>
                )
            },
            {
                title: '操作',
                align: 'right',
                render: (text, record) => (
                    <span>
                        <a onClick={e => this.editUser(record)}>编辑</a>
                        <Divider type="vertical" />
                        <a onClick={e => this.delete(record)}>删除</a>
                        <Divider type="vertical" />
                        <a onClick={e => this.resetPassword(record)}>重置密码</a>
                    </span>
                )
            }
        ];
        // 选中表格选项
        const rowSelection = {
            onSelect: (record, selected, selectedRows) => {
                console.log(record, selected, selectedRows);
                this.props.SystemStore.selectedRows = selectedRows
            },
            onSelectAll: (selected, selectedRows, changeRows) => {
                this.props.SystemStore.selectedRows = selectedRows
            }
        };
        const { getFieldDecorator } = this.props.form;
        return <div >
            <TableLayout rowKey="id" rowSelection={rowSelection} onChange={this.pageChange} loading={isLoading} columns={columns} dataSource={tableData} pagination={PageInfo} />
            {this.state.visible && <GlobalModal
                title='重置密码'
                visible={this.state.visible}
                onOk={this.handleSubmit}
                width={'50%'}
                onCancel={e => this.setState({ visible: false })}
                children={
                    <Form onSubmit={this.handleSubmit} layout='horizontal'>
                        <Form.Item label="密码">
                            {getFieldDecorator('password', {
                                rules: [{ required: true, message: '请输入密码' }],
                                initialValue: ""
                            })(
                                <Input placeholder="请输入密码" type='password' />
                            )}
                        </Form.Item>
                    </Form>
                } />}
        </div>;
    }
    pageChange = (e) => {
        this.props.SystemStore.PageInfo = e
        this.props.SystemStore.PageInfo.pageIndex = e.current
        const { getData, PageInfo, deptId, name, columnsType } = this.props.SystemStore
        getData(3, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, name, deptId, isSelf: columnsType })
    }
    //提交表单
    handleSubmit = e => {
        e.preventDefault();
        const { getData, resetPassword, formData, PageInfo, name, deptId, columnsType } = this.props.SystemStore
        this.props.form.validateFields((err, values) => {
            console.log(values)
            if (!err) {
                let obj = {}
                // obj["userDO.userId"] = formData.id
                // obj.pwdNew = values.password
                resetPassword({ pwdNew: values.password, userDO: { id: formData.id } }).then(res => {
                    console.log(res)
                    if (!res.code) {
                        message.success('重置成功')
                    } else {
                        message.error('网络错误，请稍后重试')
                    }
                    this.setState({
                        visible: false
                    })
                    getData(3, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, name, deptId, isSelf: columnsType })
                })
            }
        });
    };
    resetPassword = (record) => {
        this.props.SystemStore.formData = record
        this.setState({
            visible: true
        })
    }
    editUser = (record) => {
        let obj = record
        this.props.SystemStore.getUserRole({ id: record.id }).then(res => {
            obj.roleIds = res
            this.props.SystemStore.modalChange(1, obj)
        })
    }
    delete = (record, ) => {
        const { deleteUser, getData, PageInfo, deptId, name, columnsType } = this.props.SystemStore
        Modal.confirm({
            title: '提示',
            content: '是否确认删除该条数据？',
            okText: '确认',
            cancelText: '取消',
            onOk: () => {
                deleteUser(1, { id: record.id }).then(res => {
                    message.success('删除成功')
                    getData(3, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, name, deptId, isSelf: columnsType })
                })
            },
        });

    }
    componentDidMount() {
        const { getData, PageInfo, deptId, name } = this.props.SystemStore
        getData(3, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, name, deptId })
    }
}
const UserTableForm = Form.create({ name: 'user' })(UserTable);
export default UserTableForm;
