/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-04 12:43:55
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-04 13:42:35
 * @FilePath: \bl-device-manage-test\src\layouts\FormEdit\self_item\link_item.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import React, { useState } from "react";
import { Input, Select } from "antd";

export const Link_item = ({ value, onChange, ...rest }) => {
    const {Option} = Select
    const [select,setSelect] = useState('self')
    const handleChange = (value) => {
        onChange("")
        setSelect(value)
    }
    const handleInput = (event) => {
        onChange(event.target.value)
        console.log(event.target.value);
    }
    return (
        <div style={{display:'flex',flexDirection:'column',width:'100%'}}>
            <Select
                value={select}
                onChange={handleChange}
            >
                <Option value='self'>自定义</Option>
                <Option value='link'>数据联动</Option>
                <Option value='edit'>公式编辑</Option>
            </Select>
            <br />
            {
                select == 'self' && 
                <Input onChange={handleInput}/>
            }
            {
                select == 'edit' && 
                <div style={{alignItems:'center'}}> 
                    编辑公式
                </div>
            }
        </div>
    )
}
