/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-05 10:16:45
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-05 11:06:24
 * @FilePath: \bl-device-manage-test\src\layouts\FormEdit\changeTool.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { firstFormName } from "constants/status_constant";

export function exChange(item,firstFormId){
    let params = {};
    let pro = {};
    let subForms = [];
    params.formId = firstFormId;
    params.formName = firstFormName[firstFormId]
    for (const key in item) {
      if (key != 'properties') {
        pro[key] = item[key];
      }else{
        let field = [];
        let obj = {};
        obj['name'] = 'root';
        for (const keyP in item[key]) {
          let iObj = {};
          if(keyP.hasOwnProperty('fieldId')){
            iObj['fieldId'] = keyP['fieldId'];
          }
          iObj['name'] = keyP
          iObj['typeId'] = keyP.split('_')[0]
          iObj['detailJson'] = item[key][keyP]
          field.push(iObj);
        }
        obj['fields'] = field
        subForms.push(obj)
      }
    }
    params['properties'] = pro
    params['subForms'] = subForms
    return params;
}

export function restore(obj){
    let formItem = {}
    obj['form']['formFields']?.console
    console.log(obj['form']['formFields']);
    console.log(obj);
}
