import React from "react";
import {
  Chart,
  Geom,
  Axis,
  Tooltip,
  Legend,
} from "bizcharts";

class Curved extends React.Component {
  render() {
    const data = this.props.data
    const cols = {
      month: {
        range: [0, 1]
      },
      temperature:{
        ticks:[0,20,40,60,80,100]
      }
    };
    return <Chart height={200} data={data} scale={cols} forceFit padding={['auto',50,'auto',80]}>
      <Legend  />
      <Axis name="month" />
      <Axis
        name="temperature"
        label={{
          formatter: val => `${val}`
        }}
      />
      <Tooltip
        crosshairs={{
          type: "y"
        }}
      />
      <Geom
        type="line"
        position="month*temperature"
        size={2}
        color={["city",['#6236FF','#997CFF','#32C5FF','#89DEFF','#FF2A8E','#FF2A8E4d']]}
        shape={"smooth"}
      />
    </Chart>;
  }
}

export default Curved
