import React from 'react'
import { inject, observer } from 'mobx-react'
import { toJS } from 'mobx'
import { Tabs } from 'antd'
import DeviceList from './DeviceList'
import DeviceDefine from './DeviceDefine'
import './index.less'
const { TabPane } = Tabs;
@inject('DeviceDefineStore')
@observer
class DefinePage extends React.Component {
  constructor(props) {
    super(props)
    this.store = this.props.DeviceDefineStore;

  }
  render() {
    const { current, stepList } = toJS(this.store);
    return <div className='define-container'>

      <Tabs activeKey={current} onChange={this.onStepChange}>
          <TabPane tab={<div><i className="iconfont icon-type icon-spacing"></i>类型管理</div>} key="1" />
          <TabPane tab={<div><i className="iconfont icon-number icon-spacing"></i>型号管理</div>} key="2" />
          <TabPane tab={<div><i className="iconfont icon-list icon-spacing"></i>设备列表</div>} key="3" />
      </Tabs>
      {/* 
      <Tabs activeKey={current} onChange={this.onStepChange}>
        {
          stepList.map(item => {
            return <TabPane key={item.id} tab={item.title} >
            </TabPane>
          })
        }
      </Tabs>
      */}
      <div className="tab-content-bg">
        {
          current === '3' ? <DeviceList /> : <DeviceDefine />
        }
      </div>
    </div>;
  }
  onStepChange = (step) => {
    this.store.current = step;
    this.store.changeStep();
    this.store.selectedRowKeys = []
    this.store.searchName = ''
    if (['1', '2'].includes(step)) {
      this.store.getType({})
    } 
  }
  componentDidMount() {
    this.store.queryType = 1;
    this.store.getType({})
  }
  componentWillUnmount() {
    this.store.devList = []
    this.store.devPage = {}
    this.store.searchName = ''
  }
}

export default DefinePage
