import React from 'react';
import {
  Chart,
  Geom,
  Axis,
  Tooltip,
  Legend,
} from 'bizcharts';

class Stacked extends React.Component {
  render() {
    const data = this.props.data
    const cols = {
      value: {
        ticks:[0,20,40,60,80,100]
      },
    };
    return (
      <div>
        <Chart height={200} data={data} scale={cols} forceFit padding={['auto',50,'auto',80]}>
          <Axis name="year" />
          <Axis name="value" />
          <Legend />
          <Tooltip />
          <Geom
            type="area"
            adjustType="stack"
            position={"year*value"}
            color={["country", ['#6236FF','#997CFF','#32C5FF','#89DEFF','#FF2A8E','#FF2A8E4d']]}
          />
        </Chart>
      </div>
    );
  }
}

export default Stacked
