import React from 'react';
import { Form } from '@ant-design/compatible';
import '@ant-design/compatible/assets/index.css';
import { Input, Checkbox, Button } from 'antd';
import { withRouter } from 'react-router-dom';
import { inject, observer } from 'mobx-react';
import { setCookie, getCookie, clearCookie } from 'utils/dataTools';
import './index.less';

@withRouter
@inject('HomeStore')
@observer
class LoginPage extends React.Component {
  render() {
    const { getFieldDecorator } = this.props.form;
    return <div className='login_container'>
      <div className='login_form'>
        <div className='basic_login'>
          <div className='login_logo'>
            <img src='https://assets-boranet.oss-cn-hangzhou.aliyuncs.com/img/login_logo.png' alt='博拉科技' />
          </div>
          <div>
            <div className='login_title' style={{ paddingTop: 27, paddingBottom: 5, color: '#161616', letterSpacing: 10 }}>设备协同管理</div>
          </div>
          <div style={{ height: 4, width: 80, background: 'rgba(98, 54, 255, 1)', marginTop: 17 }}></div>
          <Form onSubmit={this.handleSubmit} >
            <Form.Item>
              {getFieldDecorator('username', {
                rules: [{ required: true, message: '请输入您的用户名！' }],
              })(
                <Input
                  size="large"
                  prefix={<i className='iconfont icon-user' style={{ color: '#9D9D9D' }} ></i>}
                  placeholder="用户名"
                  style={{ maxWidth: 290 }}
                />,
              )}
            </Form.Item>
            <Form.Item>
              {getFieldDecorator('password', {
                rules: [{ required: true, message: '请输入您的密码!' }],
              })(
                <Input
                  size="large"
                  prefix={<i className='iconfont icon-password' style={{ color: '#9D9D9D' }}></i>}
                  type="password"
                  placeholder="密码"
                  style={{ maxWidth: 290 }}
                />,
              )}
            </Form.Item>
            <Form.Item>
              {/*getFieldDecorator('unique', {
                rules: [{ required: true, message: '请输入您的公司标识!' }],
              })(
                <Input
                  size="large"
                  prefix={<i className='iconfont icon-id' style={{ color: '#9D9D9D' }}></i>}
                  placeholder="公司标识"
                  style={{ maxWidth: 290 }}
                />,
              )*/}
            </Form.Item>
            <Form.Item>
              {getFieldDecorator('remember', {
                valuePropName: 'checked',
                initialValue: true,
              })(<Checkbox>记住密码</Checkbox>)}
              <div className='login-btn'>
                <Button type="primary" style={{ maxWidth: 290 }} htmlType="submit" className="login-form-button">
                  登录
              </Button>
              </div>
            </Form.Item>
          </Form>
        </div>
      </div>

    </div>;
  }
  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        if (values.remember) {
          for (let name in values) {
            setCookie(name, values[name]);
          }
        } else {
          for (let name in values) {
            clearCookie(name);
          }
        }
        // 请求登陆接口
        this.props.HomeStore.setLogin({
          username: values.username,
          password: values.password
        }, (result) => {
          const { success, token } = result;
          if (success) {
            sessionStorage.setItem('selfToken', token); // 设置前局登陆token
            sessionStorage.setItem('username', values.username); // 设置登录用户名
            this.props.history.push({
              pathname: '/index',
              state: { username: values.username, password: values.password, selfToken: token }
            });
          }
        })
      }
    });
  };
  componentDidMount() {
    this.props.form.setFieldsValue({ 'username': getCookie('username') || '' });
    this.props.form.setFieldsValue({ 'password': getCookie('password') || '' });
    this.props.form.setFieldsValue({ 'unique': getCookie('unique') || '' });
    this.props.form.setFieldsValue({ 'remember': getCookie('remember') || '' });
  }

}
const WrappedLoginForm = Form.create({ name: 'login' })(LoginPage);
export default WrappedLoginForm;
