import React from 'react'
import { inject, observer } from 'mobx-react';
import { LeftOutlined } from '@ant-design/icons';
import { Button, DatePicker, Card, Timeline, Empty } from 'antd';
import moment from 'moment';
// import Qrcode from './Qrcanvas';
import './index.less'

@inject('StatisticsStore')
@observer
class StatisticsManage extends React.Component {
  constructor(props) {
    super(props)
    this.state = {}
  }
  render() {
    const { RangePicker } = DatePicker;
    const { detailInfo, searchDate, workInfo } = this.props.StatisticsStore;
    return (
      <div className='u-content'>
        <Card title="员工绩效分析"  >
          <div className='UserTitle'>
            <div className='u-title-left'>
              <Button style={{ marginRight: 10 }} onClick={e => this.props.history.goBack()}><LeftOutlined /></Button>
              {/* 日期：<RangePicker onChange={this.changeDate} value={[searchDate[0] ? moment(searchDate[0]) : "", searchDate[1] ? moment(searchDate[1]) : ""]} placeholder={['开始时间', '结束时间']} />
              <Button type='primary' style={{ margin: 10 }} onClick={this.onSearch}>
                查询
    </Button>*/}
              <div className='u-userName'>{detailInfo.name}</div>
            </div>
            <div className='u-title-right'>
              <p>
                工单完成率：<span>{detailInfo.rate}%</span>
              </p>
            </div>
          </div>
          <div className='userDetail'>
            <div className='u-detail-left'>
              <div className='u-tips-content'>
                <div className='user-tips'>
                  <div className='u-tips-title'>工号</div>
                  <div>{detailInfo.username}</div>
                </div>
                <div className='user-tips'>
                  <div className='u-tips-title'>维修次数</div>
                  <div>{detailInfo.maintainCount}</div>
                </div>
                <div className='user-tips'>
                  <div className='u-tips-title'>维修用时</div>
                  <div>{detailInfo.hours}</div>
                </div>
                <div className='user-tips'>
                  <div className='u-tips-title'>平均用时</div>
                  <div>{detailInfo.avgHours}</div>
                </div>
              </div>
            </div>
          </div>
          <div className='u-log-content'>
            <p style={{ fontWeight: 600, fontSize: 20 }}>工作日志：</p>
            <Timeline style={{ marginTop: 50 }}>
              {workInfo.length ? workInfo.map(item => (<Timeline.Item>
                <div className='time-line-content'>
                  <div className='right'>
                    <div>
                      <span>{item.operateUser}</span>
                    </div>
                    <ul style={{ padding: 0 }}>
                      <li>工单号：{item.workDO.workNo}</li>
                      <li>故障描述：{item.workDO.description}</li>
                      <li>{item.details&&item.details.map(log => (<div>{log.workLogDO.operateType}：{log.workLogDO.createTime}</div>))}</li>
                      <li>创建时间：{item.workDO.createTime}</li>
                      <li>完成时间：{item.workDO.expecteTime}</li>
                    </ul>
                  </div>
                </div>
              </Timeline.Item>)) : <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} />}
            </Timeline>,
    </div>
        </Card>
      </div>
    );
  }
  onSearch = () => {
    const { searchDate, getAllWorkInfo, detailInfo } = this.props.StatisticsStore
    getAllWorkInfo({ userId: detailInfo.userId, startDate: searchDate[0], endDate: searchDate[1] })
  }
  changeDate = (e, formate) => {
    this.props.StatisticsStore.searchDate = formate
  }
  handelClick = (e) => {
    console.log(e)
    this.props.StatisticsStore.columnsType = e.id
  }

}
export default StatisticsManage;
