import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Modal, message, Badge } from 'antd';
import TableLayout from 'Components/TableLayout'
import { toJS } from 'mobx';


@inject('SystemStore')
@observer
class MenuTable extends Component {
    render() {
        const { menuData, PageInfo, isLoading } = toJS(this.props.SystemStore)
        //定义表头
        const columns = [
            {
                title: '名称',
                dataIndex: 'name',
                key: 'name',
            },
            // {
            //     title: '图标',
            //     dataIndex: 'icon',
            //     key: 'icon',
            //     render: (text) => (
            //         <div> {text}</div>
            //         // <i class={text} style={{ width: 12, height: 12 }}></i>
            //     )
            // },
            {
                title: '类型',
                dataIndex: 'type',
                key: 'type',
                render: (text, record) => (
                    <div>
                        {text === 1 ? <Badge color='#6236FF' text="菜单" /> : text === 2 ? <Badge color='#FACC14' text="按钮" /> : <Badge color='#009764' text="目录" />}
                    </div>
                )
            },
            // {
            //     title: '地址',
            //     dataIndex: 'url',
            //     key: 'url',
            // },
            {
                title: '权限标识',
                dataIndex: 'perms',
                key: 'perms',
            },
            // {
            //     title: '操作',
            //     align: 'right',
            //     // render: (text, record) => (
            //     //     <span>
            //     //         <a onClick={e => this.props.SystemStore.modalChange(1, record)}>编辑</a>
            //     //         {/*<Divider type="vertical" />
            //     //             <a onClick={e => this.delete(record)}>删除</a>
            //     // <Divider type="vertical" />
            //     //         <a onClick={e => this.addLower(record)}>增加下级</a>*/}
            //     //     </span>
            //     // )
            // }
        ];

        return <div style={{ marginTop: 20 }}>
            <TableLayout rowKey="id" loading={isLoading} columns={columns} dataSource={menuData} pagination={PageInfo} />
        </div>;
    }
    addLower = (record) => {
        this.props.SystemStore.formData = {}
        this.props.SystemStore.formData.parentId = record.id
        this.props.SystemStore.modalVisible = true
    }
    delete = (record) => {
        const { deleteMenu, getMenuData, } = this.props.SystemStore
        Modal.confirm({
            title: '提示',
            content: '是否确认删除该条数据？',
            okText: '确认',
            cancelText: '取消',
            onOk: () => {
                deleteMenu({ id: record.id }).then(res => {
                    message.success('删除成功')
                    getMenuData()
                })
            },
        });

    }
    componentDidMount() {
        const { getMenuData, } = this.props.SystemStore
        getMenuData()
    }
}
export default MenuTable;
