import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Form, Select, Row, Col, Button, DatePicker } from 'antd';
const { Option } = Select;
const { RangePicker } = DatePicker;
const formItemLayout = {
	labelCol: { span: 6 },
	wrapperCol: { span: 12 }
};
@inject('DeviceDefineStore')
@observer
class SearchBar extends Component {
	constructor(props) {
		super(props);
		this.store = this.props.DeviceDefineStore;
		this.state = {
			visible: false,
			startTime: '',
			endTime: '',
			rangeTime: ''
		};
	}

	render() {
		const { getFieldDecorator } = this.props.form;
		const { customerList, selectType, selectModel, selectDevice } = this.store;

		return (
			<div className='list_search'>
				<Form className='ant-advanced-search-form'>
					<Row gutter={24}>
						<Col span={8}>
							<Form.Item label='设备类型' {...formItemLayout}>
								{getFieldDecorator('typeId')(
									<Select
										style={{ width: 220 }}
										placeholder='选择设备类型'
										onChange={this.onSelectCondition.bind(this, 'model')}
										allowClear
										showSearch
										optionFilterProp="children"
										filterOption={(input, option) =>
											option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
										}
									>
										{selectType.length > 0 &&
											selectType.map(item => (
												<Option key={item.id} value={item.id}>
													{item.typeName}
												</Option>
											))}
									</Select>
								)}
							</Form.Item>
						</Col>
						<Col span={8}>
							<Form.Item label='设备型号' {...formItemLayout}>
								{getFieldDecorator('modelId')(
									<Select
										style={{ width: 220 }}
										placeholder='选择设备型号'
										onChange={this.onSelectCondition.bind(this, 'device')}
										allowClear
										showSearch
										optionFilterProp="children"
										filterOption={(input, option) =>
											option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
										}
									>
										{selectModel.length > 0 &&
											selectModel.map((item, index) => (
												<Option key={index} value={JSON.stringify(item)}>
													{item.modelName}
												</Option>
											))}
									</Select>
								)}
							</Form.Item>
						</Col>
						<Col span={8}>
							<Form.Item label='设备名称' {...formItemLayout}>
								{getFieldDecorator('deviceId')(
									<Select
										style={{ width: 220 }}
										placeholder='选择设备'
										// onChange={this.onSelectCondition.bind(this,'deviceId')}
										allowClear
										showSearch
										optionFilterProp="children"
										filterOption={(input, option) =>
											option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
										}
									>
										{selectDevice.length > 0 &&
											selectDevice.map(item => (
												<Option key={item.id} value={item.id}>
													{item.deviceName}
												</Option>
											))}
									</Select>
								)}
							</Form.Item>
						</Col>
					</Row>
					<Row gutter={24}>
						<Col span={8}>
							<Form.Item label='创建时间' {...formItemLayout}>
								<RangePicker
									value={this.state.rangeTime}
									style={{ width: 220 }}
									locale={window.GLOBAL_PARAMS.locale}
									onChange={this.onTimeChange}
								/>
							</Form.Item>
						</Col>
						<Col span={8}>
							<Form.Item label='合作伙伴' {...formItemLayout}>
								{getFieldDecorator('customerId')(
									<Select
										showSearch
										optionFilterProp='children'
										style={{ width: 220 }}
										placeholder='选择合作伙伴'
										allowClear
										filterOption={(input, option) =>
											option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
										  }
									>
										{customerList.length > 0 &&
											customerList.map(item => (
												<Option key={item.id} value={item.id}>
													{item.customerName}
												</Option>
											))}
									</Select>
								)}
							</Form.Item>
						</Col>
						<Col span={8} style={{ textAlign: 'center', paddingLeft: '10px' }}>
							<Button type='primary' onClick={this.handleSearch} className="btn-border-purple">
								查询
							</Button>
							<Button
								style={{ marginLeft: 8, fontSize: '14px' }}
								onClick={this.handleReset}
								className="btn-border-gray"
							>
								重置
							</Button>
						</Col>
					</Row>
					{/* <Row>
						<Col span={24}>
							<Button
								type='primary'
								style={{ marginLeft: 8, fontSize: '14px' }}
								onClick={this.handleAdd}
							>
								新增
							</Button>
						</Col>
					</Row> */}
				</Form>
			</div>
		);
	}
	onSelectCondition = (type, value) => {
		console.log(value)
		if (type === 'model') {
			this.store.getSelectModel({ typeId: value });
			this.store.selectDevice = []
			this.store.selectDevice = []
			this.props.form.setFieldsValue({
				modelId: undefined,
				deviceId: undefined,
			});
		} else {
			value = JSON.parse(value)
			this.store.getSelectDevice({
				typeId: value.typeId,
				modelId: value.id
			});
			this.store.selectDevice = []
			this.props.form.setFieldsValue({
				deviceId: undefined,
			});
		}
	};
	onTimeChange = (value, string) => {
		this.setState({
			startTime: string[0],
			endTime: string[1],
			rangeTime: value
		});
	};
	handleSearch = () => {
		const { startTime, endTime } = this.state;
		this.props.form.validateFieldsAndScroll((err, values) => {
			console.log(values)
			values.modelId = values.modelId !== undefined ? values.modelId.id : ''
			this.store.searchCondition = { ...values, startTime, endTime };
			this.store.getDevList();
		});
	};
	handleReset = () => {
		this.props.form.setFieldsValue({
			typeId: undefined,
			modelId: undefined,
			deviceId: undefined,
			deviceTime: undefined,
			customerId: undefined
		});
		this.store.selectModel = []
		this.store.selectDevice = []
		this.setState({
			rangeTime: ''
		});
		this.store.searchCondition.startTime = '';
		this.store.searchCondition.endTime = '';
	};
	handleAdd = () => {
		this.store.configVisible = true;
		this.store.configStatus = 'add';
	};

	componentDidMount() {
		this.props.DeviceDefineStore.getSelectType({});
		//获取客户名称
		this.store.getCustomerList();
	}
}
const WrapperClass = Form.create({})(SearchBar);
export default WrapperClass;
