import { List, message } from 'antd'
import React, { Component } from 'react'
import VirtualList from 'rc-virtual-list';
import { inject, observer } from 'mobx-react';


@inject('MessageStore')
@observer
class index extends Component {
    render() {
        const ContainerHeight = 800;
        const {modalVisible} = this.props.MessageStore
        const onScroll = e => {
            if (e.target.scrollHeight - e.target.scrollTop === ContainerHeight) {
                this.appendData();
            }
        };
        const handleClick = (data,data2) =>{
            console.log(data);
            console.log(data2);
        }
        return (
            <List>
                <VirtualList
                    data={this.props.MessageStore.data}
                    height={ContainerHeight}
                    itemHeight={47}
                    itemKey="email"
                    onScroll={onScroll}
                >
                    {item => (
                        <List.Item key={321} extra={<div>21321</div>} onClick={() => handleClick(index,key)} style={{border:'1px solid grey',margin:'5px',padding:'4px'}}>
                            <div>
                                <div>
                                    维修工单：1
                                </div>
                                <div>
                                    维修工单：1
                                </div>
                                <div>
                                    维修工单：1
                                </div>
                            </div>
                            <div>
                                <div>
                                    11
                                </div>
                            </div>
                            <div>
                                <div>
                                    11
                                </div>
                            </div>
                        </List.Item>
                    )}
                </VirtualList>
            </List>
        )
    }
}

export default index
