import React from 'react';
import { inject, observer } from 'mobx-react';
import { Button, Select, } from 'antd';
import { setDeviceStandingColumns } from '../methods';
import Tablelayout from 'components/TableLayout';
import DailyPage from '../DailyPage'
import '../index.less';

const { Option } = Select;
@inject('DeviceDefineStore')
@observer
class DeviceStanding extends React.Component {
	constructor(props) {
		super(props);
		this.store = this.props.DeviceDefineStore;
		this.state = {
			typeId: undefined,
			modelId: undefined,
			deviceId: undefined
		};
	}
	render() {
		const {
			selectType,
			selectModel,
			selectDevice,
			devStandPage,
			devStandList,
			devStandCols,
			isDevLoading,
			isDaily
		} = this.store;
		return (
			<div  className="tab-content-bg">
				{isDaily ? (
					<DailyPage />
				) : (
						<div>
							<div className='search_bar'>
								<Select
									value={this.state.typeId}
									style={{ width: 230 }}
									placeholder='选择设备类型'
									onChange={this.onSelectType}
									allowClear
									showSearch
									optionFilterProp="children"
									filterOption={(input, option) =>
										option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
									}
								>
									{selectType.length > 0 &&
										selectType.map(item => (
											<Option key={item.id} value={item.id}>
												{item.typeName}
											</Option>
										))}
								</Select>
								<Select
									value={this.state.modelId}
									style={{ width: 230, margin: '0 10px' }}
									placeholder='选择设备型号'
									onChange={this.onSelectModel}
									allowClear
									showSearch
									optionFilterProp="children"
									filterOption={(input, option) =>
										option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
									}
								>
									{selectModel.length > 0 &&
										selectModel.map(item => (
											<Option key={item.id} value={item.id}>
												{item.modelName}
											</Option>
										))}
								</Select>
								<Select
									value={this.state.deviceId}
									style={{ width: 230, marginRight: '10px' }}
									placeholder='选择设备名称'
									onChange={this.onSelectDev}
									allowClear
									showSearch
									optionFilterProp="children"
									filterOption={(input, option) =>
										option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
									}
								>
									{selectDevice.length > 0 &&
										selectDevice.map(item => (
											<Option key={item.id} value={item.id}>
												{item.deviceName}
											</Option>
										))}
								</Select>
								<Button type='primary' onClick={this.handleSearch} className="btn-border-purple">
									查询
							</Button>
							</div>
							<div style={{ marginTop: '20px' }}>
								<Tablelayout
									rowKey='id'
									loading={isDevLoading}
									dataSource={devStandList}
									pagination={{
										...devStandPage,
										onChange: (pageIndex, pageSize) => {
											this.store.getDevStanding({
												...this.state,
												pageIndex,
												pageSize
											});
										}
									}}
									columns={setDeviceStandingColumns({
										cols:devStandCols,
										handleDailyDetail: this.handleDailyDetail
									})}
								/>
							</div>
						</div>
					)}
			</div>
		);
	}
	handleDailyDetail = record => {
		this.store.currentDaily = record;
		this.store.isDaily = true;
	};
	onSelectType = typeId => {
		this.setState({ typeId });
		if (typeId) {
			this.store.getSelectModel({ typeId });
		} else {
			this.store.selectModel = []
		}
		this.store.selectDevice = []
		this.setState({ modelId: undefined, deviceId: undefined })
	};
	onSelectModel = modelId => {
		this.setState({ modelId });
		if (modelId) {
			this.store.getSelectDevice({ modelId });
		}
		this.store.selectDevice = []
		this.setState({ deviceId: undefined })
	};
	onSelectDev = deviceId => {
		this.setState({ deviceId });
	};
	handleSearch = () => {
		this.store.getDevStanding({ ...this.state, queryType: '' });
	};
	componentDidMount() {
		this.store.getSelectType();
		this.store.getDevStanding({ ...this.state, queryType: '' });
	}
	componentWillUnmount() {
		this.store.isDaily = false;
		this.store.selectModel = [];
		this.store.selectDevice = [];
	}
}

export default DeviceStanding;
