import React from "react";
import {
  Chart,
  Geom,
  Axis,
  Tooltip,
  Legend,
} from "bizcharts";
import DataSet from "@antv/data-set";

class Groupedcolumn extends React.Component {
  state = {
    dataValue : []
  }
  componentDidMount(){
    let dataValue = []
    for (let key = -1; key > -6; key--) {
			dataValue.push(`${this.getDay(key)}`);
    }
    dataValue = dataValue.reverse()
    this.setState({dataValue})
  }
  getDay = day => {
		let today = new Date();
		let targetday_milliseconds = today.getTime() + 1000 * 60 * 60 * 24 * day;
		today.setTime(targetday_milliseconds); //注意，这行是关键代码
		let tYear = today.getFullYear();
		let tMonth = today.getMonth();
		let tDate = today.getDate();
		const doHandleMonth = month => {
			let m = month;
			if (month.toString().length == 1) {
				m = '0' + month;
			}
			return m;
		};
		tMonth = doHandleMonth(tMonth + 1);
		tDate = doHandleMonth(tDate);
		return (tYear + '' + tMonth + '' + tDate).toString();
	};
  render() {
    const data = this.props.data;
    const ds = new DataSet();
    const dv = ds.createView().source(data);
    // `${this.state.dataValue[0]}`,`${this.state.dataValue[1]}`,`${this.state.dataValue[2]}`,`${this.state.dataValue[3]}`,`${this.state.dataValue[4]}`
    dv.transform({
      type: "fold",
      fields: [...this.state.dataValue],
      // 展开字段集
      key: "月份",
      // key字段
      value: "月均降雨量" // value字段
    });
    return (
      <div>
        <Chart height={200} data={dv} forceFit>
          <Axis name="月份" />
          <Axis name="月均降雨量" 
            label={{
              formatter: val => val !== '0' ? `${val}%` : val
            }} 
          />
          <Legend />
          <Tooltip
            crosshairs={{
              type: "y"
            }}
          />
          <Geom
            type="interval"
            position="月份*月均降雨量"
            color={["name",['#6236FF','#997CFF','#32C5FF','#89DEFF','#FF2A8E','#FF2A8E4d']]}
            adjust={[
              {
                type: "dodge",
                marginRatio: 1 / 32
              }
            ]}
          />
        </Chart>
      </div>
    );
  }
}
export default Groupedcolumn
