/*
 * @Author: your name
 * @Date: 2021-11-02 14:29:58
 * @LastEditTime: 2022-01-27 12:51:07
 * @LastEditors: your name
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage\src\layouts\StatisticsManage\DataPage\index.js
 */
import React from 'react'
import { inject, observer } from 'mobx-react';
import { Button, Row, Tabs, DatePicker } from 'antd';
// import Category from 'Components/Category'
import StatisticsDialog from './StatisticsDialog'
import StatisticsTable from './StatisticsTable'
import moment from 'moment';

@inject('StatisticsStore')
@observer
class StatisticsManage extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      list: [{ id: 1, name: '设备故障分析' }, { id: 2, name: '备件分析' }, { id: 3, name: '人员绩效分析' }]
    }
  }
  render() {
    const { RangePicker } = DatePicker;
    const { TabPane } = Tabs;
    const { modalVisible, columnsType, searchDate, pageInfo } = this.props.StatisticsStore
    return <div>
      {/* <Category title='数据统计' defaultActiveKey={this.state.list[this.props.StatisticsStore.columnsType - 1]} notoggle={true} content={this.state.list} keyName='name' onClick={this.handelClick} />*/}
      <Tabs activeKey={columnsType.toString()} onChange={this.changeTabs} centered={true}>
        <TabPane tab={<div><i className="iconfont icon-spacing icon-equipmentfailure"></i>设备故障分析</div>} key='1' />
        <TabPane tab={<div><i className="iconfont icon-spacing icon-spareparts"></i>备件分析</div>} key='2' />
        <TabPane tab={<div><i className="iconfont icon-spacing icon-checktype"></i>点检分析</div>} key='3' />
        <TabPane tab={<div><i className="iconfont icon-spacing icon-performance"></i>人员绩效分析</div>} key='4' />
      </Tabs>
      <div className="tab-content-bg">
        <Row>
          统计日期：
          <RangePicker onChange={this.changeDate} value={[searchDate[0] ? moment(searchDate[0]) : "", searchDate[1] ? moment(searchDate[1]) : ""]} placeholder={['开始时间', '结束时间']} />
          <Button type='primary' style={{ margin: 10 }} onClick={this.onSearch} className="btn-border-purple"> 查询</Button>
          {<Button style={{ margin: 10 }} onClick={this.export} className="btn-border-gray"> Excel导出</Button>}
          {/*<Button style={{ margin: 10 }} > <a href='/manage/statistics/device/export'>Excel导出</a></Button>*/}
        </Row>
         <StatisticsTable history={this.props.history} />
        {modalVisible && <StatisticsDialog />}
      </div>
    </div>;
  }
  export = () => {
    const { pageInfo, columnsType, exportDate, searchDate } = this.props.StatisticsStore
    exportDate(columnsType, { pagenIndex: pageInfo.pageIndex, pageSize: pageInfo.pageSize, startDate: searchDate[0], endDate: searchDate[1] })
  }
  onSearch = () => {
    const { getData, pageInfo, columnsType, searchDate } = this.props.StatisticsStore
    getData(columnsType, { pageIndex: pageInfo.pageIndex, pageSize: pageInfo.pageSize, startDate: searchDate[0], endDate: searchDate[1] })
  }
  changeDate = (e, formate) => {
    this.props.StatisticsStore.searchDate = formate
  }
  changeTabs = (e) => {
    this.props.StatisticsStore.columnsType = Number(e)
    this.props.StatisticsStore.searchDate = []
    this.props.StatisticsStore.tableData = []
    // this.props.StatisticsStore.pageInfo = { pageIndex: 1, pageSize: 10 }
    const { getData, pageInfo, columnsType, searchDate } = this.props.StatisticsStore
    getData(columnsType, { pageIndex: 1, pageSize: 10, startDate: searchDate[0], endDate: searchDate[1] })
  }
  componentDidMount() {
    const { getData, pageInfo, columnsType, searchDate } = this.props.StatisticsStore
    getData(columnsType, { pageIndex: pageInfo.pageIndex, pageSize: pageInfo.pageSize, startDate: searchDate[0], endDate: searchDate[1] })
  }
}
export default StatisticsManage;
