import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { toJS } from 'mobx';
import { ClockCircleOutlined } from '@ant-design/icons';
import { Form } from '@ant-design/compatible';
import '@ant-design/compatible/assets/index.css';
import { Row, Col, Empty, Timeline, Button, Divider } from 'antd';

const formItemLayout = {
	labelCol: { span: 6 },
	wrapperCol: { span: 16 }
};
@inject('DeviceDefineStore')
@observer
class DetailList extends Component {
	constructor(props) {
		super(props);
		this.store = this.props.DeviceDefineStore;
	}

	render() {
		const { deviceLog, currentDaily, deviceLogPage } = toJS(this.store);
		const logTypeName = {
			1:'点检',
			2:'巡检',
			3:'保养',
			4:'故障'
		}
		return (
            <div className='device_log'>
			<div className='daily-form'>
				<h4>设备信息</h4>
				<Form layout='horizontal'>
					<Row gutter={24}>
						<Col span={8}>
							<Form.Item {...formItemLayout} label='设备名称'>
								<span>{currentDaily.deviceName || '-'}</span>
							</Form.Item>
						</Col>
						<Col span={8}>
							<Form.Item {...formItemLayout} label='设备编号'>
								<span>{currentDaily.deviceCode || '-'}</span>
							</Form.Item>
						</Col>
						<Col span={8}>
							<Form.Item {...formItemLayout} label='设备类型'>
								<span>{currentDaily.typeName || '-'}</span>
							</Form.Item>
						</Col>
					</Row>
					<Row gutter={24}>
						<Col span={8}>
							<Form.Item {...formItemLayout} label='设备型号'>
								<span>{currentDaily.modelName || '-'}</span>
							</Form.Item>
						</Col>
						<Col span={8}>
							<Form.Item {...formItemLayout} label='生产线'>
								<span>{currentDaily.productLine || '-'}</span>
							</Form.Item>
						</Col>
						<Col span={8}>
							<Form.Item {...formItemLayout} label='站点信息'>
								<span>{currentDaily.productSite || '-'}</span>
							</Form.Item>
						</Col>
					</Row>
				</Form>
			</div>
				<Divider dashed />
				{deviceLog.length > 0 ? (
						<div style={{marginTop:'20px'}}>
							<h4>设备运维日志</h4>
							<div className='time-line-all' style={{ marginLeft: '40px',marginTop:'20px' }}>
								<Timeline>
									{deviceLog.map((item, index) => {
										return (
                                            <Timeline.Item
												key={index}
												dot={
													<ClockCircleOutlined style={{ fontSize: '16px' }} />
												}
												color='red'
											>
												<div className='time-line-content'>
													<h4>{item.logTypeStr}</h4>
													<div>
														<span>时间：</span>
														<span>{item.createTime}</span>
													</div>
													<div>
														<span>执行类型：</span>
														<span>{logTypeName[item.logType]}</span>

														{
															item.logType === 2 && <span 
																style={{marginLeft:'20px',cursor:'pointer',color:'#6236FF'}}
																onClick={this.handleDetail.bind(this,item.id)}>点击查看详情</span>
														}
													</div>
													<div>
														<span>备注内容：</span>
														<span>{item.remark}</span>
													</div>
												</div>
											</Timeline.Item>
                                        );
									})}
								</Timeline>
								{deviceLogPage &&
									parseInt(deviceLogPage.total) /
										parseInt(deviceLogPage.pageIndex) >
										5 && (
										<div>
											<Button type='primary' onClick={this.onQueryMore}>
												查看更多五条记录
											</Button>
										</div>
									)}
							</div>
						</div>
				) : (
					<div
						style={{
							marginTop: '20px',
						}}
					>
						<Empty image={Empty.PRESENTED_IMAGE_SIMPLE} 
							description={
								<span>
									当前设备暂时没有运维日志
								</span>
							}
						/>
					</div>
				)}
			</div>
        );
	}
	handleDetail = (id)=>{
		this.props.handleDetail(id)
	}
	onQueryMore = () => {
		this.store.getDeviceLog({
			deviceId: this.store.currentDaily.id,
			typeId: this.store.currentDaily.typeId,
			pageSize: 5,
			pageIndex: this.store.pageIndex + 1
		});
	};
	componentDidMount() {
		console.log(this.store.currentDaily);
		this.store.getDeviceLog({
			deviceId: this.store.currentDaily.id,
			typeId: this.store.currentDaily.typeId,
			pageSize: 5,
			pageIndex: 1
		});
	}
}
export default DetailList;
