/*
 * @Author: your name
 * @Date: 2022-03-31 23:08:16
 * @LastEditTime: 2022-05-05 20:28:24
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\stores\SpareStore
 */
import { observable, action } from 'mobx';
import { isDataExist } from '../utils/dataTools';
import * as services from '../services/design';

class Message {
    @observable data = ['11', '23']
    @observable modalVisible = false
    @observable todoCount = 0
    @observable createCount = 0
    @observable handleCount = 0
    @observable copyCount = 0
    @observable todoList = []
    @observable createList = []
    @observable handleList = []
    @observable copyList = []


    @action setData(arr) {
        this.data = arr
    }

    @action changeModal() {
        this.modalVisible = !this.modalVisible;
    }

    @action.bound addList(value, list) {
        // console.log(this[value]);
        this[value].push(list);
        switch (value) {
            case 'todoList':
                this['todoCount'] = this['todoList'].length
                break;
            case 'createList':
                this['createCount'] = this['createList'].length
                break;
            case 'handleList':
                this['handleCount'] = this['handleList'].length
                break;
            case 'copyList':
                this['copyCount'] = this['copyList'].length
                break;
            default:
                break;
        }
    }

    @action.bound delList(value,flowLogId){
        console.log(this[value]);
        // this[value].push(list);
        this[value] = this[value].map((item) => {
            return item.flowLogId != flowLogId;
        })
        switch (value) {
            case 'todoList':
                this['todoCount'] = this['todoList'].length
                break;
            case 'createList':
                this['createCount'] = this['createList'].length
                break;
            case 'handleList':
                this['createCount'] = this['handleList'].length
                break;
            case 'copyList':
                this['createCount'] = this['copyList'].length
                break;
            default:
                break;
        }
    }

    @action.bound clearList() {
        this['todoCount'] = 0;
        this['todoList'] = [];
        this['createCount'] = 0;
        this['createList'] = []; 
        this['handleCount'] = 0;
        this['handleList'] = []; 
        this['copyCount'] = 0;
        this['copyList'] = [];
    }
}

let MessageStore = new Message();
export default MessageStore;
