/*
 * @Author: your name
 * @Date: 2022-03-22 09:57:59
 * @LastEditTime: 2022-04-10 23:14:27
 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage\src\layouts\TableEdit\index.js
 */

import React, { Component } from 'react';
import { Tag, Layout, Button, Modal, message, Form, Input, Select, Menu } from 'antd';
// Default SortableJS
import Sortable from 'react-sortablejs';
import _ from 'lodash';
import uniqueId from 'lodash/uniqueId';
import update from 'immutability-helper';
import { indexToArray, getItem, setInfo, isPath, getCloneItem, itemRemove, itemAdd, changeObj } from '../../utils/loashTools';
import { formItemData, GlobalComponent } from './config';
import EditableTable from './EditableTable/index';
import './index.css'
import { NavLink } from 'react-router-dom';
import { MenuObj } from 'constants/configs';
import { toJS } from 'mobx';
import { checkCurrentMenu, isDataExist } from 'utils/dataTools';
import * as services from '../../services/home';
import { typeName } from 'constants/status_constant';
import { inject, observer } from 'mobx-react';


const { Header, Sider, Content } = Layout;
const { Option } = Select;
const sortableOption = {
  animation: 20,
  fallbackOnBody: true,
  swapThreshold: 0.65,
  group: {
    name: 'formItem',
    pull: true,
    put: true,
  },
};


@inject('BasicStore')
@inject('HomeStore')
@observer
class EditPage extends Component{
  render(){
    const {itemDataT, firstFormId, secondFormId} = this.props.HomeStore;                  // 组件数据
    const isShowModal = false;            // 弹框是否显示
    let curItemKey = '';             // 当前选中组件的Key
    let curItemName = '';           // 当前选中组件的Name
    let curItemType = '';           // 当前选中组件的Type
    let isChoose = false;                  // 组件是否处于选中状态
    let obj = {}
    const handleSubmit = () => {

    };
  
    const handlePreview = () => {
      this.isChoose = false
      this.isShowModal = true
    };
  
    const handleLabelChange = (e) => {
      const val = e.target.value;
      this.curItemName = val
      obj[curItemKey].label = val;
      obj = obj
    }
  
    const handleDel = () => {
      let newTreeData = itemRemove(curItemKey, obj);
      this.curItemKey = ''
      this.curItemName = ''
      this.curItemType = ''
      itemDataT = newTreeData
    }
  
    const sortableChoose = (e) => {
      console.log(e)
      this.isChoose = true
      const curKey = e.item.getAttribute('data-id');
      const curName = e.item.firstChild.innerText;
      const curType = e.item.getAttribute('type');
      this.curItemKey = curKey
      this.curItemName = curName
      this.curItemType = curType
    };
  
    // 拖拽的添加方法
    const sortableAdd = e => {
      // 组件名或路径
      const nameOrIndex = e.clone.getAttribute('data-id');
      // 父节点路径
      const parentPath = e.path[1].getAttribute('data-id');
      // 拖拽元素的目标路径
      const { newIndex } = e;
      // 新路径 为根节点时直接使用index
      const newPath = parentPath ? `${parentPath}-${newIndex}` : newIndex;
      console.log('nameOrIndex:' + nameOrIndex, 'parentPath:' + parentPath, 'newIndex:' + newIndex, 'newPath:' + newPath)
      // 判断是否为路径 路径执行移动，非路径为新增
      if (isPath(nameOrIndex)) {
        // 旧的路径index
        const oldIndex = nameOrIndex;
        // 克隆要移动的元素
        const dragItem = getCloneItem(oldIndex, itemData)
        // 比较路径的上下位置 先执行靠下的数据 再执行靠上数据
        if (indexToArray(oldIndex) > indexToArray(newPath)) {
          // 删除元素 获得新数据
          let newTreeData = itemRemove(oldIndex, itemData);
          // 添加拖拽元素
          newTreeData = itemAdd(newPath, newTreeData, dragItem)
          // 更新视图
          this.itemDataT = newTreeData
          return
        }
        // 添加拖拽元素
        let newData = itemAdd(newPath, itemDataT, dragItem)
        // 删除元素 获得新数据
        newData = itemRemove(oldIndex, newData);
        this.itemDataT = newTreeData
        return
      }
  
      // 新增流程 创建元素 => 插入元素 => 更新视图
      const id = nameOrIndex
      const newItem = _.cloneDeep(formItemData.find(item => (item.name === id)))
      // 为容器或者弹框时增加子元素
      if (newItem.name === 'Containers') {
        const ComponentsInfo = _.cloneDeep(GlobalComponent[newItem.name])
        // 判断是否包含默认数据
        newItem.children = [ComponentsInfo]
      }
      let Data = itemAdd(newPath, itemData, newItem)
      this.itemDataT = Data
    };
  
    // 拖拽的排序方法
    const sortableUpdate = e => {
      // 交换数组
      const { newIndex, oldIndex } = e;
      // 父节点路径
      const parentPath = e.path[1].getAttribute('data-id');
      // 父元素 根节点时直接调用data
      let parent = parentPath ? getItem(parentPath, itemData) : itemData;
      // 当前拖拽元素
      const dragItem = parent[oldIndex];
      // 更新后的父节点
      parent = update(parent, {
        $splice: [[oldIndex, 1], [newIndex, 0, dragItem]],
      });
      // 最新的数据 根节点时直接调用data
      const Data = parentPath ? setInfo(parentPath, itemData, parent) : parent
      // 调用父组件更新方法
      this.itemDataT = Data
    };
    
    // 递归函数
    const loop = (arr, index) => {
      return (
        arr.map((item, i) => {
          const indexs = index === '' ? String(i) : `${index}-${i}`;
          if (item) {
            if (item.children) {
              return (
                <div {...item.attr} data-id={indexs} key={indexs}>
                  <Sortable
                    key={uniqueId()}
                    style={{ minHeight: 100, margin: 10 }}
                    ref={c => c && c.sortable}
                    options={{
                      ...sortableOption,
                      onUpdate: e => sortableUpdate(e),
                      onAdd: e => sortableAdd(e),
                      onChoose: e => sortableChoose(e),
                      onSort: e => setIsChoose(false),
                    }}
                  >
                    {loop(item.children, indexs)}
                  </Sortable>
                </div>
              )
            }
            const ComponentInfo = GlobalComponent[item.name]
            return (
              <div
                data-id={indexs}
                key={indexs}
                type={item.name}
                className='formItemStyle'
                style={(isChoose && indexs === curItemKey) ? { border: '1px solid #FF3333' } : {}}
              >
                {
                  item.name !== 'Divider' &&
                  <div className='formItemLabel'>{isChoose ? (indexs === curItemKey ? curItemName : item.label) : item.label}</div>
                }
                {
                  renderDiffComponents(item, indexs, ComponentInfo)
                }
              </div>
            )
          } else {
            return null
          }
        })
      )
    };
  
    const renderDiffComponents = (item, indexs, ComponentInfo) => {
      switch (item.name) {
        case 'Divider':
          return <ComponentInfo key={indexs} {...item.attr}></ComponentInfo>
        case 'Select':
          return (
            <ComponentInfo key={indexs} defaultValue={item.attr.value}>
              {
                item.attr.options.map(subItem => <Option key={subItem.key} value={subItem.value + ''}>{subItem.label}</Option>)
              }
            </ComponentInfo>
          )
        default:
          return <ComponentInfo key={indexs} {...item.attr} />
      }
    }
  
    const getDataSource = (options) => {
      obj[curItemKey].attr.options = [...options];
      this.obj = obj
    }
  
    const save = (params) => {
      Modal.confirm({
        title: '提示',
        content: '是否修改该表单',
        okText: '确认',
        cancelText: '取消',
        onOk() {
          try {
            let res = services.putRequest(services.requestList.updataField, params);
            message.success('修改成功')
          } catch (error) {
            console.log(error);
          }
  
        },
        onCancel() {
          console.log('Cancel');
        },
      });
  
    }
  
    const saveForm = () => {
      let text = []
      obj.map((element, index) => {
        text.push(changeObj(firstFormId, 0, element, index))
      });
      console.log(text);
      save(text)
    }
    return (
      <Layout>
        <Header className="header">
          <NavLink to='/basic'><Button type='dashed' class='backButton'>Back</Button></NavLink>
          <Menu theme="light" mode="horizontal" defaultSelectedKeys={['2']} justify='center' class='headerMenu'>
            <Menu.Item key="1">nav 1</Menu.Item>
            <Menu.Item key="2">nav 2</Menu.Item>
            <Menu.Item key="3">nav 3</Menu.Item>
          </Menu>
          <Button onClick={saveForm}>保存</Button>
        </Header>
        <Layout>
          <Sider width={'15%'} style={{ height: '100%', borderLeft: 10, minHeight: 840, marginTop: 20, textAlign: 'center' }} theme='light'>
            <h3 className='textHead'>组件列表</h3>
            <Sortable
              options={{
                group: {
                  name: 'formItem',
                  pull: 'clone',
                  put: false,
                },
                sort: false,
              }}
            >
              {
                formItemData.map(item => (
                  <div
                    data-id={item.name}
                    key={item.name}
                    style={{ marginTop: 10 }}
                  >
                    <Tag>{item.label + '-' + item.name}</Tag>
                  </div>
                ))
              }
            </Sortable>
          </Sider>
          <Layout style={{ padding: '0 24px 24px', marginTop: 20 }}>
            <Content
              className="site-layout-background"
              style={{
                padding: 24,
                margin: 0,
                minHeight: 300,
              }}
            >
              <Sortable
                className='formContent'
                ref={c => c && c.sortable}
                options={{
                  ...sortableOption,
                  onUpdate: e => sortableUpdate(e),
                  onAdd: e => sortableAdd(e),
                  onChoose: e => sortableChoose(e),
                  onSort: e => setIsChoose(false),
                }}
                key={uniqueId()}
                style={{ minHeight: '100%' }}
              >
                {loop(obj, '')}
              </Sortable>
            </Content>
            <Sider theme='light' width={'20%'} style={{ textAlign: 'center' }}>
              <h3 className='textHead'>字段设置</h3>
              <Form className='itemForm'>
                <Form.Item label="组件Key">
                  <Input value={curItemKey} disabled />
                </Form.Item>
                <Form.Item label="标签名">
                  <Input value={curItemName} disabled={!isChoose} onChange={handleLabelChange} />
                </Form.Item>
                {
                  ['CheckboxGroup', 'RadioGroup', 'Select'].includes(curItemType) &&
                  <EditableTable
                    getDataSource={getDataSource}
                    curItemKey={curItemKey}
                    options={itemData[curItemKey].attr.options}
                    disabled={!isChoose}
                  />
                }
              </Form>
              <Button className='delBtn' onClick={handleDel} disabled={!isChoose}>删除</Button>
            </Sider>
          </Layout>
        </Layout>
      </Layout>
    );
  }
  //转换为所需对象
  changeField = () => {
    let obj = itemDataT
    if (itemDataT.length != 0) {
      let itemData1 = itemDataT.filter(function (txt) {
        return txt.secondFormId == secondFormId
      })
      if (itemData1.length == 0) {
        return obj;
      }
      let properties = {}
      properties = toJS(itemData1[0].properties)
      properties.forEach(element => {
        if (element.propertyId != undefined) {
          let ele = {}
          ele.label = element.name
          ele.attr = element.others
          ele.propertyId = element.propertyId
          ele.name = typeName[element.typeId]
          obj.push(ele)
        }
        obj.push(element)
      });
    }
    return obj
  }
  componentDidMount(){
    obj = this.changeField();
  }


}

export default EditPage;
