import { get, post, put } from '../utils/request';

/* 存储请求地址*/
export const requestUrl = {
    departmentList: '/manage/sys/sysDept/list',//部门列表
    departmentTree: '/manage/sys/sysDept/tree',//部门列表树
    removeDepartment: '/manage/sys/sysDept/delete',//删除部门
    saveDepartment: '/manage/sys/sysDept/add',//新增部门
    updataDepartment: '/manage/sys/sysDept/update',//编辑部门
    roleList: '/manage/sys/role/list',//角色列表
    allRoleList: '/manage/sys/role/listAll',//角色列表
    saveRole: '/manage/sys/role/add',//新增角色
    updataRole: '/manage/sys/role/update',//编辑角色
    removeRole: '/manage/sys/role/delete',//删除角色
    batchRemoveRole: '/manage/sys/role/batchDelete',//批量删除角色
    treeMenu: '/manage/sys/menu/tree',//获取菜单树
    getMenuIdsByRole: '/manage/sys/menu/getMenuIdsByRole',//查询角色当前权限
    menuList: '/manage/sys/menu/list',//菜单列表
    removeMenu: '/manage/sys/menu/delete',//删除菜单
    userList: '/manage/sys/user/list',//用户列表
    saveMenu: '/manage/sys/menu/add',//新增菜单
    updateMenu: '/manage/sys/menu/update',//编辑菜单
    saveUser: '/manage/sys/user/add',//保存用户
    updataUser: '/manage/sys/user/update',//编辑用户
    removeUser: '/manage/sys/user/delete',//删除用户
    batchRemoveUser: '/manage/sys/user/batchDelete',//批量删除用户
    adminResetPwd: '/manage/sys/user/adminResetPwd',//修改用户密码
    getUserRoles: '/manage/sys/user/getRoles',//查询用户所有角色
    getCustomer: '/manage/sys/customer/list',//客户列表
    addCustomer: '/manage/sys/customer/add',//客户新增
    updateCustomer: '/manage/sys/customer/update',//客户编辑
    deleteCus: '/manage/sys/customer/delete',//客户删除
    exist: '/manage/sys/user/exist',//客户删除
}

/* 测试接口的数据 */
export async function getRequest(url, data, options) {
    return await get(url, data, options);
}

/* 测试接口的数据 */
export async function postRequest(url, data, options) {
    return await post(url, data, options);
}
/* 测试接口的数据 */
export async function putRequest(url, data, options) {
    return await put(url, data, options);
}
