import React, { Component } from 'react';
import { ClockCircleOutlined, RollbackOutlined } from '@ant-design/icons';
import { Form } from '@ant-design/compatible';
import '@ant-design/compatible/assets/index.css';
import { Timeline, Row, Col, Input } from 'antd';
import { inject, observer } from 'mobx-react';
import { workStatus } from 'constants/status_constant';
import Table from './Table';
import { Button } from 'antd';
import classnames from 'classnames';
import './index.less'


const formItemLayout = {
	labelCol: { span: 4 },
	wrapperCol: { span: 12 },
};

@inject('WorkStore')
@observer
class WorkDaily extends Component {
	constructor(props) {
		super(props);
		this.store = this.props.WorkStore;
	}
	render() {
		const { workLog, currentMain } = this.store;
		const { getFieldDecorator } = this.props.form;
		return (
            <div className="tab-content-bg">
				<div className='title'>
					<div style={{ marginBottom: '20px', borderLeft: '3px solid #6236FF', paddingLeft: '10px' }}>
						<span style={{ fontSize: '16px', fontWeight: '600', marginRight: '10px' }}>记录详情 </span>
						<Button size='small' icon={<RollbackOutlined />} onClick={() => this.goBack()}>返回</Button>
					</div>
					<div className='title_input'>
						<Row gutter={24}>
							<Col span={12}>
								<Form.Item {...formItemLayout} label="设备名称">
									{getFieldDecorator('deviceName', {
										initialValue: currentMain.deviceName
									})(
										<Input disabled placeholder='请输入' style={{ width: '250px' }} />
									)}
								</Form.Item>
							</Col>
							<Col span={12}>
								<Form.Item {...formItemLayout} label="工单状态">
									{getFieldDecorator('workStatus', {
										initialValue: workStatus[currentMain.workStatus]
									})(
										<Input disabled placeholder='请输入' style={{ width: '250px' }} />
									)}
								</Form.Item>
							</Col>
						</Row>
					</div>
				</div>
				<Timeline className='time_line_all'>
					{workLog.details && workLog.details.map((item, index) => {
						return (
                            <Timeline.Item
								key={index}
								dot={
									<ClockCircleOutlined style={{ fontSize: '16px' }} />
								}
								color='red'
							>
								<div className='time_line_content'>
									<div className='right'>
										<div className='time_line_title'>
											<span>{item.workLogDO.createTime}</span>
											<span>{item.workLogDO.operateName}</span>
										</div>
										<ul className='time_line_ul'>
											<li className='time_line_li_header'>
												<span>{item.workLogDO.operateType}</span>
												{/* {[1].includes(workLog.workDO.workStatus) && <span
													style={{
														color: '#6236FF', cursor: 'pointer'
													}}
													onClick={() => {
														this.store.detailVisible = true;
														this.store.detailLogId = workLog.workDO.id
														this.store.getDetailLog({ logId: item.workLogDetailDOS[0].id })
													}
													}>点击查看详情</span>} */}
											</li>
											{
												item.workLogDetailDOS && item.workLogDetailDOS.map(detail => {
													return <li key={detail.id}>
														<span>{detail.description}</span>
														<span>{detail.info}</span>
													</li>
												})
											}
											{
												item.fileDOS && item.fileDOS.map((detail, index) => {
													return <li key={detail.id} className={
														classnames({
															'time_line_audio': detail.fileType === 3,
														})
													}>
														{
															detail.fileType === 1 ? <span>{'参考文件'}</span> :
																detail.fileType === 2 ? <span>{'图片描述'}</span> :
																	<span>{'语音描述'}</span>
														}
														{
															detail.fileType === 1 ? <span>{detail.filename}</span> :
																detail.fileType === 2 ? <span className='time_line_img'>
																	<img src={'http://file.boranet.cn/' + detail.filename} alt='我是一张图片' width='100%' />
																</span> :
																	<span>
																		<audio src={'http://file.boranet.cn/' + detail.filename} controls='controls'>
																			您的浏览器不支持该音频文件
																		</audio>
																	</span>

														}
													</li>
												})
											}
										</ul>
									</div>
								</div>
							</Timeline.Item>
                        );
					})}
				</Timeline>
				{
					this.store.detailVisible && <Table
						visible={this.store.detailVisible}
						onCancel={() => { this.store.detailVisible = false }}
					/>
				}
			</div>
        );
	}
	goBack = () => {
		this.store.isDaily = false
	}
	onOk = () => {
		this.props.form.validateFieldsAndScroll((err, values) => {
			if (!err) {
				this.store.saveWork({
					id: this.store.currentMain.id,
					...values
				});
				this.props.onClose();
			}
		});
	};
	componentDidMount() {
		console.log(this.store.currentMain.id)
		this.store.getWorkLog({
			id: this.store.currentMain.id
		})
	}
}
const WrappedDynamicRule = Form.create({})(WorkDaily);
export default WrappedDynamicRule;
