/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-17 11:56:33
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-19 02:22:45
 * @FilePath: \bl-device-manage-test\src\layouts\FormEdit\self_item\mul_tag.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { DeleteOutlined } from "@ant-design/icons";
import { Input, Switch } from "antd";
import { inject, observer } from "mobx-react";
import React, { useEffect, useState } from "react";
import './index.css'
const Mul_Tag = observer(({ FormStore, value, onChange, ...rest }) => {
    const { addons } = rest
    const [tagList, setTagList] = useState(FormStore.subFormList)
    useEffect(() => {
        console.log(rest);
    }, [])
    const handleChange = (checked) => {
        console.log(checked);
        if (checked == true) {
            let iList = FormStore.subFormList
            iList.push({ name: '标签一', fields: [] })
            iList.push({ name: '标签二', fields: [] })
            iList.push({ name: '标签三', fields: [] })
            FormStore.setValue('subFormList', iList);
            setTagList(iList)
            onChange(iList);
        } else {
            FormStore.setValue('subFormList', FormStore.subFormList[0]);
            setTagList([])
            onChange([]);
        }
    }
    const titleChange = (value, index) => {
        let iList = [...tagList]
        let iObj = { name: value, 'fields': iList[index + 1]['fields'] }
        iList.splice(index + 1, 1, iObj)
        setTagList(iList)
        onChange(iList);
        FormStore.setValue('subFormList', iList);

    }
    const handleAdd = () => {
        let iObj = { name: '标签页', 'fields': [] }
        let iList = [...tagList]
        iList.push(iObj)
        setTagList(iList)
        onChange(iList);
        FormStore.setValue('subFormList', iList);

    }
    const handleDel = (index) => {
        let iList = [...tagList]
        iList.splice(index + 1, 1)
        setTagList(iList)
        onChange(iList);
        FormStore.setValue('subFormList', iList);

    }
    return (
        <div className="total_tags">
            <div className="tags_title">
                <div>多标签显示</div>
                <Switch onChange={handleChange} style={{ width: '20px' }}>多标签显示</Switch>
            </div>

            <div className="all_tags">
                {tagList.map((item, index) => {
                    return (
                        <div className="one_tag" key={index}>
                            <Input defaultValue={item['name']} className="one_title" onChange={(e) => titleChange(e.target.value, index)} />
                            {
                                tagList.length > 2 ? (
                                    <DeleteOutlined onClick={() => handleDel(index)} />
                                ) : (
                                    <DeleteOutlined />
                                )
                            }
                        </div>
                    )
                })}
            </div>
            {
                tagList.length > 0 && (
                    <div className="add_tag" onClick={handleAdd}>
                        +添加标签页
                    </div>
                )
            }
        </div>
    )
})
export default inject((stores) => ({ FormStore: stores.FormStore }))(Mul_Tag)
