import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Button, Row, Input, Modal, message, Tabs, Tree } from 'antd';
import Category from 'Components/Category'
import UserTable from './UserTable'
import UserDialog from './UserDialog'
const { TreeNode } = Tree;

@inject('SystemStore')
@observer
class SystemMenu extends Component {
    render() {
        const { modalVisible, modalChange, columnsType, DepList, CumList, SupList } = this.props.SystemStore
        const { TabPane } = Tabs;
        return <div>
            <Tabs defaultActiveKey="1" onChange={this.tabChange}>
                <TabPane tab={<div><i className="iconfont icon-compy icon-spacing"></i>公司</div>} key="1" />
                <TabPane tab={<div><i className="iconfont icon-cop icon-spacing"></i>合作伙伴</div>} key="2" />
                {/*<TabPane tab="供应商" key="3" />*/}
            </Tabs> 
            <div className="tab-content-bg">   
            <Row style={{marginBottom:20}}>
                <Input allowClear style={{ width: 250 }} placeholder='请输入姓名' onChange={this.searchValue}></Input>
                <Button type='primary' style={{ marginLeft: 10 }} onClick={this.search} className="btn-border-purple">查询</Button>
                <Button style={{ marginLeft: 10 }} onClick={e => modalChange(0)} className="add-btn" type='primary'>
                    <i className="iconfont icon-add icon-spacing" style={{ fontSize: '12px' }}></i>
                    新增
                </Button>
                <Button style={{ marginLeft: 10 }} onClick={this.batchRemove} className="btn-border-gray">批量删除</Button>
            </Row>

            {columnsType === '2' && <Category title='客户' keyName='customerName' content={CumList} onClick={this.handelClick} />}
            {columnsType === '3' && <Category title='供应商' keyName='customerName' content={SupList} onClick={this.handelClick} />}
            <Row style={{ display: 'flex' }}>

                {columnsType === '1' && <div style={{ width: 200, marginRight: 10 }}>
                    <Tree
                        onSelect={this.selectDept}
                        defaultExpandedKeys={['-1']}
                        blockNode
                    >
                        {this.renderTreeNodes(DepList)}
                    </Tree>
                </div>
                }
                <div style={{ flex: 1, marginTop: columnsType === '1' ? 0 : 20 }}>
                    <UserTable />
                </div>
            </Row>
            {modalVisible && <UserDialog />}
            </div>
        </div>;
    }
    searchValue = (e) => {
        this.props.SystemStore.name = e.target.value
    }
    selectDept = (e) => {
        this.props.SystemStore.deptId = e[0] === '-1' ? "" : e[0]
        const { getData, PageInfo, deptId, name } = this.props.SystemStore
        getData(3, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, deptId, name, isSelf: 1 })

    }
    //渲染树
    renderTreeNodes = data =>
        data.map(item => {
            if (item.children) {
                return (
                    <TreeNode title={item.text} key={item.id} dataRef={item}>
                        {this.renderTreeNodes(item.children)}
                    </TreeNode>
                );
            }
            return <TreeNode {...item} />;
        });
    //tab切换
    tabChange = (e) => {
        this.props.SystemStore.tableData = []
        this.props.SystemStore.columnsType = e
        this.props.SystemStore.deptId = ""
        this.props.SystemStore.PageInfo = { pageIndex: 1, pageSize: 10 }
        const { getData, PageInfo, deptId, name } = this.props.SystemStore
        getData(3, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, deptId, name, isSelf: e })
    }
    //点击客户
    handelClick = (e) => {
        console.log(e)
        this.props.SystemStore.deptId = e.id
        const { getData, PageInfo, deptId, name } = this.props.SystemStore
        getData(3, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, deptId, name, isSelf: 2 })
    }
    // 批量删除
    batchRemove = () => {
        const { deleteUser, getData, PageInfo, selectedRows, deptId, name, columnsType } = this.props.SystemStore
        if (selectedRows.length === 0) {
            message.warning('请至少选择一条数据')
            return
        }
        Modal.confirm({
            title: '提示',
            content: `是否确认删除该${selectedRows.length}条数据？`,
            okText: '确认',
            cancelText: '取消',
            onOk: () => {
                let arr = []
                for (let item of selectedRows) {
                    arr.push(item.id)
                }
                deleteUser(0, { ids: arr }).then(res => {
                    message.success('删除成功')
                    getData(3, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, deptId, name, isSelf: columnsType })
                })
            },
        });
    }
    search = () => {
        const { getData, PageInfo, deptId, name, columnsType } = this.props.SystemStore
        getData(3, { pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize, name, deptId, isSelf: columnsType })
    }
    componentDidMount() {
        const { getDept, getRoleList, getDepList, getCumList, } = this.props.SystemStore
        getRoleList()
        getDepList()
        getCumList()
        getDept()
    }
    componentWillUnmount() {
        this.props.SystemStore.clearData()
    }
}
export default SystemMenu;
