/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-06 22:04:09
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-06 22:56:48
 * @FilePath: \bl-device-manage-test\src\layouts\XTableLayout\index.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { toJS } from 'mobx'
import { inject, observer } from 'mobx-react'
import React, { useEffect } from 'react'
import { Search, Table, useTable, withTable } from 'table-render';
import stores from 'stores'



const XTableLayout = observer(({ TableStore, HomeStore }) => {
    const { columns } = TableStore
    useEffect(() => {
        console.log(toJS(columns));
        console.log(toJS(dataSource));
    }, [])
    const dataSource = [
        {
            1: "沉降车间",
            45: "测试编辑",
            49: "ullamco",
            createPerson: "Misaki",
            createTime: "2022-06-28 20:59:54",
            updateTime: "2022-07-06 21:31:42",
            key: 3
        }
    ]
    const searchApi = () => {
        return {
            rows:dataSource,
            total:dataSource.length
        }
    }
    const schema = {
        type: "object",
        labelWidth: 30,
        properties: {
            1: {
                check: {},
                title: "车间",
                type: "string",
                fieldId: 1
            },
            45: {
                check: {},
                title: "人员",
                type: "string",
                fieldId: 45
            }
        }
    }
    return (
        <>
            <Search api={searchApi} schema={schema}/>
            <Table
                pagination={{ pageSize: 3 }}
                columns={[
                    {
                        "title": "车间",
                        "dataIndex": 1,
                        "key": 1,
                        "detailJson": {
                            "check": {},
                            "title": "车间",
                            "type": "string",
                            "fieldId": 1
                        }
                    },
                    {
                        "title": "人员",
                        "dataIndex": 45,
                        "key": 45,
                        "detailJson": {
                            "check": {},
                            "title": "人员",
                            "type": "string",
                            "fieldId": 45
                        }
                    },
                    {
                        "title": "测试多条字段",
                        "dataIndex": 46,
                        "key": 46,
                        "detailJson": {
                            "check": {},
                            "title": "测试多条字段",
                            "type": "string"
                        }
                    },
                    {
                        "title": "测试多条字段",
                        "dataIndex": 47,
                        "key": 47,
                        "detailJson": {
                            "check": {},
                            "title": "测试多条字段",
                            "type": "string"
                        }
                    },
                    {
                        "title": "测试多条字段",
                        "dataIndex": 48,
                        "key": 48,
                        "detailJson": {
                            "check": {},
                            "title": "测试多条字段",
                            "type": "string"
                        }
                    },
                    {
                        "title": "测试多条字段",
                        "dataIndex": 49,
                        "key": 49,
                        "detailJson": {
                            "check": {},
                            "title": "测试多条字段",
                            "type": "string"
                        }
                    },
                    {
                        "title": "创建人",
                        "dataIndex": "createPerson",
                        "key": "createPerson"
                    },
                    {
                        "title": "创建时间",
                        "dataIndex": "createTime",
                        "key": "createTime"
                    },
                    {
                        "title": "更新时间",
                        "dataIndex": "updateTime",
                        "key": "updateTime"
                    }
                ]}
            />
        </>
    )
})

export default inject((stores) => ({ TableStore: stores.TableStore, HomeStore: stores.HomeStore }))(XTableLayout);
