import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { toJS } from 'mobx';
import GlobalList from 'components/GlobalList';
import { InfoCircleOutlined } from '@ant-design/icons';
import { Modal, Table } from 'antd';
import { omit } from 'lodash';
import { setSchemaColumns, setSchemaFormColumns, setSchemaModalColumns, } from '../cell/methods';
import { DraggableArea } from 'react-draggable-tags';

@inject('DeviceMaintainStore')
@observer
class SchemaMenu extends Component {
	constructor(props) {
		super(props);
		this.store = this.props.DeviceMaintainStore;
		this.state = {
			selectedRowKeys: [],
			modalSelectedRowKeys: [], //弹窗内部的选项
			visible: false,
			tags: [],
			checkTime: '',
		};
	}
	render() {
		const {
			schemaList,
			schemaPage,
			serverList,
			deviceList,
			isLoading,
			status,
			currentMainSchame
		} = this.store;
		const { outlineHour, outlineMinute } = toJS(this.store.currentMainSchame);
		const rowSelection = {
			selectedRowKeys: this.state.selectedRowKeys,
			onChange: this.handleSelect
		};
		return (
            <div style={{ marginTop: '10px' }}>
				<GlobalList
					checkedTag={{
						dataSource: deviceList,
						title: '设备类型',
						PopoVisible: false,
						keyName: 'typeName',
						defaultActiveKey: deviceList[0],
						content: deviceList,
						onClick: this.handleCheckTag
					}}
					table={{
						rowKey: 'id',
						columns: setSchemaColumns({
							handleDelete: this.handleDelete, handleEdit: this.handleEdit, handleStatus: this.handleStatus
						}),
						dataSource: schemaList,
						pagination: {
							...schemaPage,
							onChange: (pageIndex, pageSize) => {
								this.store.getScheme({
									pageIndex,
									pageSize,
								});
							}
						},
						loading: isLoading,
						rowSelection,
						btns: [
							{
								name: <div><i className="iconfont icon-add icon-spacing" style={{ fontSize: '12px' }}></i>新增</div>,
								onClick: () => {
									this.store.status = 'add';
									this.handleVisible();
								},
								type: 'primary',
								className: 'add-btn'
							},
							// {
							// 	name: '删除',
							// 	onClick: () => this.handleDelete({type:'batch'})
							// }
						]
					}}
					modal={{
						visible: this.state.visible,
						title: status === 'add' ? '新增' : '编辑',
						onCancel: this.handleVisible,
						onOk: this.handleSubmit,
						isForm: true,
						formColumns: setSchemaFormColumns({
							status, currentMain: currentMainSchame, maintainType: 1, onTimeChange: this.onTimeChange,
							checkTime: outlineHour + ':' + outlineMinute + ':' + '00'
						}),
						width: 700,
						otherContent: <div>
							<Table
								scroll={{ y: 300 }}
								rowKey='id'
								columns={setSchemaModalColumns({
									handleDetail: this.handleDetail,
									contentLabel: '点检内容', contentTypeLabel: '点检类型'
								})}
								dataSource={serverList}
								rowSelection={{
									selectedRowKeys: this.state.modalSelectedRowKeys,
									onChange: this.onModalItemSelect,
									selection: {
										key: 'id',
										text: 'name'
									}
								}}
								pagination={false}
								size='small'
							/>
							{
								this.state.tags.length > 0 && [
									<div style={{ fontSize: '14px', fontWeight: '600', padding: '10px' }}>
										<InfoCircleOutlined style={{ marginRight: '5px', color: '#6236FF' }} />显示顺序</div>,
									<div className='Simple-2'>
										<DraggableArea
											tags={this.state.tags}
											render={({ tag, index }) => (
												<div className='tag' key={index}>
													<img
														src={require('../../../../assets/delete.png')}
														alt='delete'
														className='delete'
														onClick={() => this.handleClickDelete(tag)}
													/>
													{tag.content}
												</div>
											)}
											onChange={this.changeTag}
										/>
									</div>
								]
							}
						</div>
					}}
				/>
			</div>
        );
	}
	/* 点击查看库中每项内容的详情 */
	handleDetail = (id) => {
		this.store.status = 'query'
		this.store.getInfo({ id }, 'server').then(() => {
			this.store.editVisible = !this.store.editVisible;
		});
	}
	handleStatus = (record) => {
		this.store.save({
			id: record.id,
			planStatus: record.planStatus === 1 ? 0 : 1,
			maintainType: 1,
		}, 'schema')
	}
	/* modal - operate */
	handleVisible = () => {
		/* 每次打开弹窗 获取所有的点检列表 */
		if (!this.state.visible) {
			this.store.getMaintainServer({}).then(() => {
				if (this.store.status == 'edit') {
					this.state.tags = []
					this.store.currentMainSchame.contentIds && this.store.currentMainSchame.contentIds.split(',').map(item => {
						let tag = toJS(this.store.serverList).filter(tag => tag.id === parseInt(item))[0]
						if (tag !== undefined) {
							this.state.tags.push(tag)
						}
					})
				} else {
					this.state.tags = []
					this.state.modalSelectedRowKeys = []
				}
				this.setState({})
			});
		}
		if (this.state.visible) {
			this.state = {
				...this.state,
				selectedRowKeys: [],
				modalSelectedRowKeys: [], //弹窗内部的选项
				tags: []
			};
		}
		this.setState({
			visible: !this.state.visible
		})
	}
	/* 弹窗内表格的操作 */
	onModalItemSelect = selectedRowKeys => {
		this.state.tags = []
		selectedRowKeys.map(item => {
			let tag = toJS(this.store.serverList).filter(tag => tag.id === item)[0]
			if (tag !== undefined) { this.state.tags.push(tag) }
		})
		this.setState({
			modalSelectedRowKeys: selectedRowKeys
		})
	}
	changeTag = tags => {
		this.setState({ tags })
	}
	handleClickDelete = (tag) => {
		const tags = this.state.tags.filter(t => tag.id !== t.id);
		let selectedRowKeys = []
		tags.map(item => {
			selectedRowKeys.push(item.id)
		})
		this.setState({ tags, modalSelectedRowKeys: selectedRowKeys });
	}
	onTimeChange = (time, timeString) => {
		this.setState({
			checkTime: timeString
		})

	}
	/* 弹窗提交操作 */
	handleSubmit = values => {
		const { deviceType, status, currentMainSchame, maintainType } = this.store;
		const { tags } = this.state;
		this.state.modalSelectedRowKeys = []
		tags.map(item => {
			this.state.modalSelectedRowKeys.push(item.id)
		})
		let params = {
			deviceType,
			contentIds: this.state.modalSelectedRowKeys.join(','),
			maintainType,
			...omit(values, 'checkTime'),
			outlineHour: this.state.checkTime.split(':')[0],
			outlineMinute: this.state.checkTime.split(':')[1]
		}
		if (status !== 'add') {
			params.id = currentMainSchame.id
		}
		this.store.save(params, 'schema');
		this.handleVisible();
	};
	/* 编辑弹窗 */
	handleEdit = value => {
		this.store.status = 'edit';
		this.store.getInfo({ id: value }, 'schema').then(() => {
			let data = this.store.currentMainSchame.contentIds.split(','), newDs = [];
			const { outlineHour, outlineMinute } = toJS(this.store.currentMainSchame);
			this.setState({
				checkTime: outlineHour + ':' + outlineMinute + ':' + '00'
			})
			data.map(item => {
				newDs.push(parseFloat(item))
			})
			console.log(newDs)
			this.setState({
				modalSelectedRowKeys: newDs
			}, () => {
				this.handleVisible();
			})
		})
	};
	/* 删除 点检方案 */
	handleDelete = ({ type, value }) => {
		if (type === 'batch') {
			if (this.state.selectedRowKeys.length < 1) {
				Modal.warn({
					title: '提示',
					content: '请选择需要删除的内容'
				});
			} else {
				Modal.confirm({
					title: '提示',
					content:
						'确定删除选中的' +
						this.state.selectedRowKeys.length +
						'条点检内容？',
					okText: '确定',
					cancelText: '取消',
					onOk: () => {
						this.store.delete(
							{
								ids: this.state.selectedRowKeys
							},
							'schema'
						);
					}
				});
			}
		} else {
			Modal.confirm({
				title: '提示',
				content: '确定删除当前点检内容？',
				okText: '确定',
				cancelText: '取消',
				onOk: () => {
					this.store.delete(
						{
							id: value
						},
						'schema'
					);
				}
			});
		}
	};
	handleSelect = selectedRowKeys => {
		this.setState({ selectedRowKeys });
	};
	/* check - tag */
	handleCheckTag = tag => {
		this.store.deviceType = tag.id;
		this.store.getMaintainSchema({
			typeId: tag.id
		});
	};
	componentDidMount() {
		this.store.getMaintainSchema({
			typeId: this.store.deviceType
		});
	}
}
export default SchemaMenu;
