import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import {
	Form,
	Input,
	Select,
	Row,
	Col,
	Button,
	Upload,
	message, Icon,
	Modal, Checkbox, Radio
} from 'antd';
import { DraggableArea } from 'react-draggable-tags';
var QRCode = require('qrcode.react');
const { Option } = Select;

const formItemLayout = {
	labelCol: { span: 0 },
	wrapperCol: { span: 6 }
};
const propertyList = [
	{ id: 1, title: '设备名称', dataIndex: 'deviceName', key: 'deviceName' },
	{ id: 2, title: '设备编号', dataIndex: 'deviceCode', key: 'deviceCode' },
	{ id: 3, title: '型号', dataIndex: 'modelId', key: 'modelId', },
	{ id: 4, title: '保修期', dataIndex: 'guaranteePeriod', key: 'guaranteePeriod', },
	{ id: 5, title: '计量单位', dataIndex: 'unit', key: 'unit', },
	{ id: 6, title: '创建时间', dataIndex: 'deviceTime', key: 'deviceTime', },
]
@inject('DeviceDefineStore')
@observer
class SearchBar extends Component {
	constructor(props) {
		super(props);
		this.store = this.props.DeviceDefineStore;
		this.state = {
			visible: false,
			status: false,
			tags: [],
			checked: [],
			typeId: undefined,
			moelId: undefined,
			deviceName: "",
			codeInfo:{},
			QRBackImg:'',
			codePosition:'left',
		};
	}

	render() {
		const { getFieldDecorator } = this.props.form;
		const { selectModel, selectType } = this.store;
		const props = {
			name: 'file',
			action: '/manage/base/device/import',
			headers: {
				authorization: 'authorization-text',
				token: sessionStorage.getItem('selfToken'),
			},
			showUploadList: false,
			accept: '.xlsx',
			onChange: this.handleImport
		};
		return (
			<div className='list_search'>
				<Row gutter={24}>
					<Col span={24}>
						<Form layout="inline">
							<Form.Item label={null} {...formItemLayout}>
								{getFieldDecorator('typeId')(
									<Select showSearch
										optionFilterProp='children'
										style={{ width: 230 }}
										placeholder='请选择设备类型'
										onChange={this.onSelectType}
										allowClear
										filterOption={(input, option) =>
											option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
										}>

										{selectType.length > 0 &&
											selectType.map(item => (
												<Option key={item.id} value={item.id}>
													{item.typeName}
												</Option>
											))}
									</Select>
								)}
							</Form.Item>
							<Form.Item label='' {...formItemLayout}>
								{getFieldDecorator('modelId')(
									<Select
										showSearch
										optionFilterProp='children'
										style={{ width: 230 }}
										placeholder='请选择设备型号'
										allowClear
										filterOption={(input, option) =>
											option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
										}>
										{selectModel.length > 0 &&
											selectModel.map(item => (
												<Option key={item.id} value={item.id}>
													{item.modelName}
												</Option>
											))}
									</Select>
								)}
							</Form.Item>
							<Form.Item label='' {...formItemLayout}>
								{getFieldDecorator('deviceName')(
									<Input style={{ width: 230 }} allowClear placeholder='请输入设备名称' />
								)}
							</Form.Item>
							<Form.Item label='' wrapperCol={{ span: 24 }}>
								<Button
									// size='small'
									className='btn-border-purple'
									style={{ fontSize: '14px' }}
									type='primary'
									onClick={this.handleSearch}
								>
									查询
									</Button>
								<Button
									// size='small'
									className='btn-border-gray'
									style={{ marginLeft: 8, fontSize: '14px' }}
									onClick={this.handleReset}
								>
									重置
									</Button>
							</Form.Item>
						</Form>
					</Col>
				</Row>
				<Row>
					<Col span={24} style={{ textAlign: 'left' }}>
						<Button
							type='primary'
							className='add-btn'
							style={{ fontSize: '14px' }}
							onClick={this.handleAdd}
						>
							<i className='iconfont icon-add icon-spacing' style={{fontSize:'12px'}}></i>
							新增
							</Button>
						{/* <Button
								size='small'
								style={{ marginLeft: 8, fontSize: '13px' }}
								onClick={() => {
									this.props.onDelete('batch');
								}}
							>
								批量删除
							</Button> */}
						<Button
							// size='small'
							type='primary'
							style={{ marginLeft: 8, fontSize: '14px' }}
							onClick={() => { window.open(`//${window.location.host}/manage/base/device/download`) }}
						>
							下载模版
							</Button>
						<Upload {...props}>
							<Button
								// size='small'
								type='primary'
								style={{ marginLeft: 8, fontSize: '14px' }}
							>
								<i className='iconfont icon-input icon-spacing' style={{fontSize:'12px'}}></i>
								导入
								</Button>
						</Upload>
						<Button
							// size='small'
							type='primary'
							style={{ marginLeft: 8, fontSize: '14px' }}
							onClick={this.handleExport}
						>
							<i className='iconfont icon-output icon-spacing' style={{fontSize:'12px'}}></i>
							导出
							</Button>
						<Button
							// size='small'
							type='primary'
							style={{ marginLeft: 8, fontSize: '14px' }}
							onClick={this.handleCode}
						>
							铭牌设置
						</Button>
						{/*<Button
								size='small'
								style={{ marginLeft: 8, fontSize: '13px' }}
								onClick={this.downloadCode}
							>
								下载铭牌
							</Button>*/}
					</Col>
				</Row>

				{this.state.visible && this.renderQRCode()}
			</div>
		);
	}
	renderQRCode = ()=>{
		const propsBtn = {
			name: 'file',
			// action: '/manage/base/device/import',
			headers: {
				authorization: 'authorization-text',
				token: sessionStorage.getItem('selfToken'),
			},
			showUploadList: false,
			onChange:this.modifyQrCodeBkg,
		};
		const renderCodeModule = ()=>{
			const arr = [
				<div style={{ width: 180, height: 180, padding: 10, background: '#fff', marginBottom: 20 }}>
					<QRCode size={160} value={this.store.codeInfo.deviceCode || '-'} />
				</div>,
				<div style={{ flex: 1, marginLeft: 20, color: '#fff', fontSize: 24, marginTop: 0, letterSpacing: 2 }}>
					{this.codeInfo()}
				</div>
			]
			return this.state.codePosition === 'right' ? arr.reverse() : arr
		}
		return <Modal
			title={'铭牌设置'}
			width={630}
			visible={this.state.visible}
			onOk={this.onCodeSubmit}
			onCancel={e => this.setState({ visible: !this.state.visible })}
			okText='确定'
			cancelText='取消'
		>
			<div className='CodeModalTitle'>
				铭牌字段（最多4个）
			</div>
			<ul className='code_ul'>
				<li>
					<span><Icon type="info-circle" />属性选择</span>
					<div>
						<Checkbox.Group style={{ width: '100%' }} value={this.state.checked} onChange={this.onPropertyChange}>
							{
								propertyList.map(item => {
									let currentChecked = this.state.tags.filter(tag => item.id === tag.id).length > 0;
									return <Checkbox disabled={this.state.tags.length >= 4 && !currentChecked} value={item.id} key={item.id} >
										{item.title}
									</Checkbox>
								})
							}

						</Checkbox.Group>
					</div>
				</li>
				<li>
					<span><Icon type="info-circle" />铭牌模板配置</span>
					<div style={{marginTop:10}}>- 属性显示顺序(可拖拽实现排序)</div>
					<div style={{marginBottom:10}}>
						{!!this.state.tags.length && (
							<div className='Simple'>
								<DraggableArea
									tags={this.state.tags}
									render={({ tag, index }) => (
										<div className='tag' key={index}>
											<img
												src={require('../../../../assets/delete.png')}
												alt='delete'
												className='delete'
												onClick={() => this.handleClickDelete(tag)}
											/>
											{tag.title}
										</div>
									)}
									onChange={this.changeTag}
								/>
							</div>
						)}
					</div>
					<div style={{margin:'10px 0 0 '}}>- 模板布局配置</div>
					<div>
						<div style={{display:'inline-block'}}>二维码位置设置：</div>
						<Radio.Group onChange={this.handleCodePos} value={this.state.codePosition}>
							<Radio value={'left'}>左</Radio>
							<Radio value={'right'}>右</Radio>
						</Radio.Group>
					</div>
					<div style={{margin:'10px 0'}}>- 模板预览</div>
					{	
						this.store.codeInfo && <div className='qrcode-name'>
							<div id="my-node" style={{ background: `url(${this.state.QRBackImg || 'https://assets-boranet.oss-cn-hangzhou.aliyuncs.com/img/bg.png'})`, backgroundSize: '100%', backgroundRepeat: 'no-repeat', padding: '20px 20px 0 40px', borderRadius: '12px' }}>
								<div style={{ display: 'flex', }}>
									{renderCodeModule()}
								</div>
							</div>
						</div>
					}
				</li>
			</ul>

		</Modal>
	}

	/* 铭牌设置模板 */
	codeInfo = () => {
		let arr = []
    for (let tag of this.state.tags) {
			arr.push(<p style={{ marginBottom: 10 }}>{tag.title}：--</p>)
    }
    return arr
	}
	/* 修改二维码图片的位置 */
	handleCodePos = e =>{
		this.setState({
			codePosition:e.target.value
		})
	}
	/* 修改上传的二维码背景图片 */
	modifyQrCodeBkg = ({file})=>{
    if (file.status === 'done') {
			this.setState({
				QRBackImg:'http://file.boranet.cn/'+file.name
			})
    } else if (file.status === 'error') {
      message.error(`${file.name} file upload failed.`);
    }
	}

	//拖动tag
	changeTag = tags => {
		this.setState({ tags: tags });
	};
	//删除tag
	handleClickDelete = (tag) => {
		const tags = this.state.tags.filter(t => tag.id !== t.id);
		let checked = []
		tags.map(item=>{
			checked.push(item.id)
		})
		this.setState({ tags,checked });
	}
	onPropertyChange = values => {
		let tags = [],codeInfo = {}
		if (values.length > 4) {
			message.warn('最多可选4个')
		} else {
			values.map(value => {
				propertyList.map(item => {
					if (value === item.id) {
						tags.push(item)
						codeInfo[item.key] = '---'
					}
				})
			})
			this.setState({
				tags,codeInfo,checked:values
			})
		}
	}
	onCodeSubmit = () => {
		let arr = []
		this.state.tags.map((item, index) => {
			arr.push({ item: item.key, sort: index })
		})
		arr.push({item:this.state.codePosition,sort:-100})
		this.store.saveSortList({ qrcodeDOList: arr })
		this.handleCode()
	}
	handleCode = () => {
		this.props.DeviceDefineStore.getSortList().then(() => {
			let arr = []
			let arr2 = []
			this.props.DeviceDefineStore.sortList.map(item => {
				propertyList.map(tag => {
					if (item.item === tag.key) {
						arr.push(tag)
						arr2.push(tag.id)
					}
				})
				if(item.sort === -100){
					this.state.codePosition = item.item
				}
			})
			console.log('获取二维码信息：', this.store.qrcodeInfo)
			// this.state.QRBackImg = 'http://file.boranet.cn' + this.store.qrcodeInfo.backgroundUrl
			this.setState({ tags: arr, checked: arr2, visible: !this.state.visible })
		})
	};
	handleSearch = () => {
		this.props.form.validateFieldsAndScroll((err, values) => {
			if (!err) {
				this.store.searchCondition = values;
				this.store.getDevList({
					...values
				});
			}
		});
	};
	handleReset = () => {
		this.props.form.setFieldsValue({
			deviceName: undefined,
			modelId: undefined,
			typeId: undefined
		});
	};
	handleAdd = () => {
		this.store.listVisible = true;
		this.store.listStatus = 'add';
	};
	download = () => {
		this.store.downloadModule();
	};
	handleImport = info => {
		if (info.file.status !== 'uploading') {
			// console.log(info.file, info.fileList);
			this.store.getDevList();
		}
		if (info.file.status === 'done') {
			if (info.file.response.code) {
				message.error(info.file.response.msg);
			} else {
				message.success(`${info.file.name} file uploaded successfully`);
			}
		} else if (info.file.status === 'error') {
			message.error(`${info.file.name} file upload failed.`);
		}
	};
	handleExport = () => {
		const { devListSelectedRowKeys } = this.props.DeviceDefineStore
		if (devListSelectedRowKeys.length) {
			const url = `/manage/base/device/export?ids=${devListSelectedRowKeys.join(',')}`
			window.open(url)
		} else {
			message.warning('请至少选择一条数据')
		}

	};
	downloadCode = () => {
		console.log('下载二维码');
	};
	onSelectType = value => {
		if (value) this.store.getSelectModel({ typeId: value });
		this.props.form.setFieldsValue({
			deviceName: undefined,
			modelId: undefined
		});
		this.store.selectModel = []
	};
	componentDidMount() {
		this.store.getSelectType({});
	}
}
const WrapperClass = Form.create({})(SearchBar);
export default WrapperClass;
